AND D3.VST_CNFMDT BETWEEN TO_CHAR(TO_DATE(#{strtDt}, 'YYYYMMDD') + 1, 'YYYYMMDD') AND TO_CHAR( TO_DATE(#{strtDt}, 'YYYYMMDD') + ( SELECT SPP_PRD FROM TB_SVPD_SDING_CNR_SPP_DOW_IZ WHERE CNR_OG_CD = D4.OG_CD AND DTA_DL_YN = 'N' AND DOW_DV_CD = TO_CHAR(TO_DATE(#{strtDt}, 'YYYYMMDD'), 'D') - 1 AND #{strtDt} BETWEEN APY_STRTDT AND APY_ENDDT AND ROWNUM = 1 ), 'YYYYMMDD')
AND D1.REFRI_DV_CD = #{refriDivCd}
AND D1.ITM_IOST_DV_CD = #{sppDvCd}
AND D3.WK_PRGS_STAT_CD IN ('00', '10') /* 작업대기, 진행중 */
AND D3.WK_PRGS_STAT_CD = #{fshProcsCd}
AND EXISTS ( SELECT 1 FROM TB_PDBS_PD_REL S1 INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL S2 ON S2.PD_CD = S1.BASE_PD_CD WHERE S1.DTA_DL_YN = 'N' AND S2.DTA_DL_YN = 'N' AND S2.PD_EXTS_PRP_GRP_CD = 'SPP' AND TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') BETWEEN S1.VL_STRT_DTM AND S1.VL_END_DTM AND S1.OJ_PD_CD = D2.PD_CD AND S2.PD_PRP_VAL12 = #{pkgDvCd} )
AND D1.OSTR_CNFMDT IS NOT NULL
AND D1.OSTR_CNFMDT IS NULL
UNION ALL
SELECT D1.CNTR_NO /* 계약번호 */ , D1.CNTR_SN /* 계약일련번호 */ , D1.SV_BIZ_HCLSF_CD /* 서비스업무대분류코드 */ , D1.SV_BIZ_DCLSF_CD /* 서비스업무세분류코드 */ , D1.SDING_SPP_NO /* 배송주문번호 */ , D1.SDING_SPP_SN /* 배송계획일련번호 */ , D1.REFRI_DV_CD /* 유무상구분코드 */ , D1.OSTR_CNFMDT /* 출고확정일자 */ , D1.ITM_IOST_DV_CD /* 품목입출고구분코드 */ , D1.SDING_PKG_PD_CD /* 배송 패키지 상품코드 */ , D5.PDCT_PD_CD /* 현재 패키지 상품코드 */ , D5.REQD_DT /* 철거일자 */ , SUBSTR(D2.FST_RGST_DTM, 1, 8) AS RCPDT /* 접수일자 */ , D3.VST_CNFMDT /* 방문일자 */ , D1.OSTR_DUEDT /* 출고예정일자 */ , D3.WK_EXCN_DT AS BS_FSH_DT /* B/S완료일자 */ , D1.DP_DT /* 입금일자 */ , D4.OG_NM /* 방문센터 */ , D4.OG_TP_CD /* 조직유형코드 */ , D4.PRTNR_NO /* 파트너번호 */ , D5.SV_PD_CD /* 서비스상품코드 */ , D1.MNGR_DV_CD /* 관리자구분코드 */ , D1.DP_EPTT_NM /* 입금예정자명 */ , D1.RECAP_CS_AMT /* 유상비용금액 */ , D3.CST_SV_ASN_NO /* 고객서비스배정번호 */ , D2.VST_DUEDT /* 방문예정일자 */ , ( SELECT T.SDING_PD_CD || '|' || T.PD_NM || '|' || T.SDING_QTY || '|' || T.SOW_DT FROM ( SELECT S1.SDING_PD_CD , S2.PD_NM , S1.SDING_QTY , CASE WHEN S3.PD_PRP_VAL19 = '6' THEN '' ELSE TO_CHAR(TO_DATE(D3.VST_CNFMDT, 'YYYYMMDD') - TO_NUMBER(S3.PD_PRP_VAL06), 'YYYYMMDD') END AS SOW_DT , ROW_NUMBER() OVER(ORDER BY S1.SDING_SN) AS RN FROM TB_SVPD_SDING_SPP_EXP_IZ S1 /* 모종배송예정내역 */ INNER JOIN TB_PDBS_PD_BAS S2 /* 상품기본 */ ON S2.PD_CD = S1.SDING_PD_CD INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL S3 /* 상품각사속성상세 */ ON S3.PD_CD = S2.PD_CD WHERE S1.CNTR_NO = D1.CNTR_NO AND S1.CNTR_SN = D1.CNTR_SN AND S1.SV_BIZ_HCLSF_CD = D1.SV_BIZ_HCLSF_CD AND S1.SV_BIZ_DCLSF_CD = D1.SV_BIZ_DCLSF_CD AND S1.SDING_SPP_NO = D1.SDING_SPP_NO AND S1.SDING_SPP_SN = D1.SDING_SPP_SN AND S1.DTA_DL_YN = 'N' AND S2.DTA_DL_YN = 'N' AND S2.PD_TP_CD = 'M' AND S3.DTA_DL_YN = 'N' AND S3.PD_EXTS_PRP_GRP_CD = 'PART'
AND S3.PD_PRP_VAL20 = '11' /* 모종 */ AND S3.PD_PRP_VAL19 IN ('3', '4') /* 모종, 상품 */
) T WHERE T.RN = 1 ) AS SDING_INFO1 /* 모종1 정보 */ , ( SELECT T.SDING_PD_CD || '|' || T.PD_NM || '|' || T.SDING_QTY || '|' || T.SOW_DT FROM ( SELECT S1.SDING_PD_CD , S2.PD_NM , S1.SDING_QTY , CASE WHEN S3.PD_PRP_VAL19 = '6' THEN '' ELSE TO_CHAR(TO_DATE(D3.VST_CNFMDT, 'YYYYMMDD') - TO_NUMBER(S3.PD_PRP_VAL06), 'YYYYMMDD') END AS SOW_DT , ROW_NUMBER() OVER(ORDER BY S1.SDING_SN) AS RN FROM TB_SVPD_SDING_SPP_EXP_IZ S1 /* 모종배송예정내역 */ INNER JOIN TB_PDBS_PD_BAS S2 /* 상품기본 */ ON S2.PD_CD = S1.SDING_PD_CD INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL S3 /* 상품각사속성상세 */ ON S3.PD_CD = S2.PD_CD WHERE S1.CNTR_NO = D1.CNTR_NO AND S1.CNTR_SN = D1.CNTR_SN AND S1.SV_BIZ_HCLSF_CD = D1.SV_BIZ_HCLSF_CD AND S1.SV_BIZ_DCLSF_CD = D1.SV_BIZ_DCLSF_CD AND S1.SDING_SPP_NO = D1.SDING_SPP_NO AND S1.SDING_SPP_SN = D1.SDING_SPP_SN AND S1.DTA_DL_YN = 'N' AND S2.DTA_DL_YN = 'N' AND S2.PD_TP_CD = 'M' AND S3.DTA_DL_YN = 'N' AND S3.PD_EXTS_PRP_GRP_CD = 'PART'
AND S3.PD_PRP_VAL20 = '11' /* 모종 */ AND S3.PD_PRP_VAL19 IN ('3', '4') /* 모종, 상품 */
) T WHERE T.RN = 2 ) AS SDING_INFO2 /* 모종2 정보 */ , ( SELECT T.SDING_PD_CD || '|' || T.PD_NM || '|' || T.SDING_QTY || '|' || T.SOW_DT FROM ( SELECT S1.SDING_PD_CD , S2.PD_NM , S1.SDING_QTY , CASE WHEN S3.PD_PRP_VAL19 = '6' THEN '' ELSE TO_CHAR(TO_DATE(D3.VST_CNFMDT, 'YYYYMMDD') - TO_NUMBER(S3.PD_PRP_VAL06), 'YYYYMMDD') END AS SOW_DT , ROW_NUMBER() OVER(ORDER BY S1.SDING_SN) AS RN FROM TB_SVPD_SDING_SPP_EXP_IZ S1 /* 모종배송예정내역 */ INNER JOIN TB_PDBS_PD_BAS S2 /* 상품기본 */ ON S2.PD_CD = S1.SDING_PD_CD INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL S3 /* 상품각사속성상세 */ ON S3.PD_CD = S2.PD_CD WHERE S1.CNTR_NO = D1.CNTR_NO AND S1.CNTR_SN = D1.CNTR_SN AND S1.SV_BIZ_HCLSF_CD = D1.SV_BIZ_HCLSF_CD AND S1.SV_BIZ_DCLSF_CD = D1.SV_BIZ_DCLSF_CD AND S1.SDING_SPP_NO = D1.SDING_SPP_NO AND S1.SDING_SPP_SN = D1.SDING_SPP_SN AND S1.DTA_DL_YN = 'N' AND S2.DTA_DL_YN = 'N' AND S2.PD_TP_CD = 'M' AND S3.DTA_DL_YN = 'N' AND S3.PD_EXTS_PRP_GRP_CD = 'PART'
AND S3.PD_PRP_VAL20 = '11' /* 모종 */ AND S3.PD_PRP_VAL19 IN ('3', '4') /* 모종, 상품 */
) T WHERE T.RN = 3 ) AS SDING_INFO3 /* 모종3 정보 */ , ( SELECT T.SDING_PD_CD || '|' || T.PD_NM || '|' || T.SDING_QTY || '|' || T.SOW_DT FROM ( SELECT S1.SDING_PD_CD , S2.PD_NM , S1.SDING_QTY , CASE WHEN S3.PD_PRP_VAL19 = '6' THEN '' ELSE TO_CHAR(TO_DATE(D3.VST_CNFMDT, 'YYYYMMDD') - TO_NUMBER(S3.PD_PRP_VAL06), 'YYYYMMDD') END AS SOW_DT , ROW_NUMBER() OVER(ORDER BY S1.SDING_SN) AS RN FROM TB_SVPD_SDING_SPP_EXP_IZ S1 /* 모종배송예정내역 */ INNER JOIN TB_PDBS_PD_BAS S2 /* 상품기본 */ ON S2.PD_CD = S1.SDING_PD_CD INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL S3 /* 상품각사속성상세 */ ON S3.PD_CD = S2.PD_CD WHERE S1.CNTR_NO = D1.CNTR_NO AND S1.CNTR_SN = D1.CNTR_SN AND S1.SV_BIZ_HCLSF_CD = D1.SV_BIZ_HCLSF_CD AND S1.SV_BIZ_DCLSF_CD = D1.SV_BIZ_DCLSF_CD AND S1.SDING_SPP_NO = D1.SDING_SPP_NO AND S1.SDING_SPP_SN = D1.SDING_SPP_SN AND S1.DTA_DL_YN = 'N' AND S2.DTA_DL_YN = 'N' AND S2.PD_TP_CD = 'M' AND S3.DTA_DL_YN = 'N' AND S3.PD_EXTS_PRP_GRP_CD = 'PART'
AND S3.PD_PRP_VAL20 = '11' /* 모종 */ AND S3.PD_PRP_VAL19 IN ('3', '4') /* 모종, 상품 */
) T WHERE T.RN = 4 ) AS SDING_INFO4 /* 모종4 정보 */ , ( SELECT T.SDING_PD_CD || '|' || T.PD_NM || '|' || T.SDING_QTY || '|' || T.SOW_DT FROM ( SELECT S1.SDING_PD_CD , S2.PD_NM , S1.SDING_QTY , CASE WHEN S3.PD_PRP_VAL19 = '6' THEN '' ELSE TO_CHAR(TO_DATE(D3.VST_CNFMDT, 'YYYYMMDD') - TO_NUMBER(S3.PD_PRP_VAL06), 'YYYYMMDD') END AS SOW_DT , ROW_NUMBER() OVER(ORDER BY S1.SDING_SN) AS RN FROM TB_SVPD_SDING_SPP_EXP_IZ S1 /* 모종배송예정내역 */ INNER JOIN TB_PDBS_PD_BAS S2 /* 상품기본 */ ON S2.PD_CD = S1.SDING_PD_CD INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL S3 /* 상품각사속성상세 */ ON S3.PD_CD = S2.PD_CD WHERE S1.CNTR_NO = D1.CNTR_NO AND S1.CNTR_SN = D1.CNTR_SN AND S1.SV_BIZ_HCLSF_CD = D1.SV_BIZ_HCLSF_CD AND S1.SV_BIZ_DCLSF_CD = D1.SV_BIZ_DCLSF_CD AND S1.SDING_SPP_NO = D1.SDING_SPP_NO AND S1.SDING_SPP_SN = D1.SDING_SPP_SN AND S1.DTA_DL_YN = 'N' AND S2.DTA_DL_YN = 'N' AND S2.PD_TP_CD = 'M' AND S3.DTA_DL_YN = 'N' AND S3.PD_EXTS_PRP_GRP_CD = 'PART'
AND S3.PD_PRP_VAL20 = '11' /* 모종 */ AND S3.PD_PRP_VAL19 IN ('3', '4') /* 모종, 상품 */
) T WHERE T.RN = 5 ) AS SDING_INFO5 /* 모종5 정보 */ FROM TB_SVPD_SDING_SPP_PLAN_IZ D1 /* 모종배송계획내역 */ INNER JOIN TB_SVPD_CST_SV_BFSVC_OJ_IZ D2 /* 고객서비스BS대상내역 */ ON D2.CNTR_NO = D1.CNTR_NO AND D2.CNTR_SN = D1.CNTR_SN AND D2.SV_BIZ_DCLSF_CD = D1.SV_BIZ_DCLSF_CD AND D2.CST_SV_ASN_NO = D1.SDING_SPP_NO INNER JOIN TB_SVPD_CST_SV_BFSVC_ASN_IZ D3 /* 고객서비스BS배정내역 */ ON D3.CST_SV_ASN_NO = D2.CST_SV_ASN_NO AND D3.ASN_OJ_YM = D2.ASN_OJ_YM LEFT OUTER JOIN TB_OGBS_MM_PRTNR_IZ D4 /* 월파트너내역 */ ON D4.OG_TP_CD = D3.CNFM_PSIC_PRTNR_OG_TP_CD AND D4.PRTNR_NO = D3.CNFM_PSIC_PRTNR_NO AND D4.DTA_DL_YN = 'N' AND D4.BASE_YM = SUBSTR(#{strtDt}, 1, 6) INNER JOIN TB_SVPD_CST_SV_EXCN_IZ D5 /* 고객서비스수행내역 */ ON D5.CNTR_NO = D1.CNTR_NO AND D5.CNTR_SN = D1.CNTR_SN INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL D6 /* 상품각사속성상세 */ ON D6.PD_CD = D2.PDCT_PD_CD WHERE D1.DTA_DL_YN = 'N' AND D1.SV_BIZ_HCLSF_CD = '2' AND D2.DTA_DL_YN = 'N' AND D3.DTA_DL_YN = 'N' AND D5.DTA_DL_YN = 'N' AND D5.CNTR_DTL_STAT_CD = '101' /* 정상 */ AND D6.DTA_DL_YN = 'N' AND D6.PD_EXTS_PRP_GRP_CD = 'PART' AND D1.CAN_DT IS NULL AND D5.REQD_DT IS NULL AND D6.PD_PRP_VAL20 = '11' /* 모종 */ AND D6.PD_PRP_VAL19 IN ('3', '4') /* 모종, 상품 */
AND D1.SV_BIZ_HCLSF_CD = #{svBizHclsfCd}
AND D2.FST_RGST_DTM BETWEEN #{strtDt} || '000000' AND #{endDt} || '235959' /* 접수일자 */
AND D3.VST_CNFMDT BETWEEN #{strtDt} AND #{endDt} /* 방문일자 */
AND D1.OSTR_CNFMDT BETWEEN #{strtDt} AND #{endDt} /* 확정일자 */
AND D3.VST_CNFMDT BETWEEN TO_CHAR(TO_DATE(#{strtDt}, 'YYYYMMDD') + 1, 'YYYYMMDD') AND TO_CHAR( TO_DATE(#{strtDt}, 'YYYYMMDD') + ( SELECT SPP_PRD FROM TB_SVPD_SDING_CNR_SPP_DOW_IZ WHERE CNR_OG_CD = D4.OG_CD AND DTA_DL_YN = 'N' AND DOW_DV_CD = TO_CHAR(TO_DATE(#{strtDt}, 'YYYYMMDD'), 'D') - 1 AND #{strtDt} BETWEEN APY_STRTDT AND APY_ENDDT AND ROWNUM = 1 ), 'YYYYMMDD')
AND D1.REFRI_DV_CD = #{refriDivCd}
AND D1.ITM_IOST_DV_CD = #{sppDvCd}
AND D3.VST_PRGS_STAT_CD IN ('00', '10') /* 작업대기, 진행중 */
AND D3.VST_PRGS_STAT_CD = #{fshProcsCd}
AND EXISTS ( SELECT 1 FROM TB_PDBS_PD_REL S1 INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL S2 ON S2.PD_CD = S1.BASE_PD_CD WHERE S1.DTA_DL_YN = 'N' AND S2.DTA_DL_YN = 'N' AND S2.PD_EXTS_PRP_GRP_CD = 'SPP' AND TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') BETWEEN S1.VL_STRT_DTM AND S1.VL_END_DTM AND S1.OJ_PD_CD = D2.PDCT_PD_CD AND S2.PD_PRP_VAL12 = #{pkgDvCd} )
AND D1.OSTR_CNFMDT IS NOT NULL
AND D1.OSTR_CNFMDT IS NULL
) T1 INNER JOIN TB_SSCT_CNTR_REL T2 /* 계약관계-(모종패키지 계약과 결합된) 웰스팜계약 */ ON T2.BASE_DTL_CNTR_NO = T1.CNTR_NO AND T2.BASE_DTL_CNTR_SN = T1.CNTR_SN INNER JOIN TB_SSCT_CNTR_DTL T3 /* 계약상세-웰스팜 */ ON T3.CNTR_NO = T2.OJ_DTL_CNTR_NO AND T3.CNTR_SN = T2.OJ_DTL_CNTR_SN INNER JOIN TB_PDBS_PD_BAS T4 /* 상품기본-웰스팜 */ ON T4.PD_CD = T3.BASE_PD_CD INNER JOIN TB_SSCT_CNTR_CST_REL T5 /* 계약고객관계-웰스팜 */ ON T5.DTL_CNTR_NO = T3.CNTR_NO AND T5.DTL_CNTR_SN = T3.CNTR_SN INNER JOIN TB_CUBS_CST_BAS T6 /* 고객기본-웰스팜 */ ON T6.CST_NO = T5.CST_NO INNER JOIN TB_PDBS_PD_BAS T7 /* 상품기본-현재패키지 */ ON T7.PD_CD = T1.PDCT_PD_CD INNER JOIN TB_PDBS_PD_BAS T8 /* 상품기본-배송패키지 */ ON T8.PD_CD = T1.SDING_PKG_PD_CD INNER JOIN TB_SSCT_CNTR_ADR_REL T9 /* 계약주소관계-모종패키지 */ ON T9.DTL_CNTR_NO = T1.CNTR_NO AND T9.DTL_CNTR_SN = T1.CNTR_SN LEFT OUTER JOIN TB_SSCT_CNTR_ADRPC_BAS T10 /* 계약주소지기본-모종패키지 */ ON T10.CNTR_ADRPC_ID = T9.CNTR_ADRPC_ID AND T10.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_OGBS_PRTNR_BAS T11 /* 파트너기본 */ ON T11.OG_TP_CD = T1.OG_TP_CD AND T11.PRTNR_NO = T1.PRTNR_NO LEFT OUTER JOIN TB_GBCO_ADR_BAS T12 /* 주소기본 */ ON T12.ADR_ID = NVL(T10.ADR_ID, T6.ADR_ID) AND T12.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_SVPD_BYZIP_SHPADR_IZ T13 /* 우편번호별배송처내역 */ ON T13.NEW_ADR_ZIP = T12.NEW_ADR_ZIP AND T13.DTA_DL_YN = 'N' WHERE T2.DTA_DL_YN = 'N' AND T2.CNTR_REL_DTL_CD = '216' /* 모종결합(웰스팜+모종) */ AND TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') BETWEEN T2.VL_STRT_DTM AND T2.VL_END_DTM AND T3.DTA_DL_YN = 'N' AND T3.CNTR_DTL_STAT_CD <> '303' /* 계약취소 */ AND T5.CNTR_CST_REL_TP_CD = '10' /* 계약자 */ AND T9.DTA_DL_YN = 'N' AND T9.ADRPC_TP_CD IN ('2', '3') /* 배달주소, 설치주소 */ AND TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') BETWEEN T9.VL_STRT_DTM AND T9.VL_END_DTM ORDER BY T1.CNTR_NO, T1.CNTR_SN, T1.SDING_SPP_NO, T1.SDING_SPP_SN

mapper.update:
UPDATE TB_SVPD_SDING_SPP_PLAN_IZ   /* 모종배송계획내역 */
   SET DP_DT = #{dpDt}

 WHERE DTA_DL_YN       = 'N'
   AND REFRI_DV_CD     = '2'   /* 유상서비스 */
   AND OSTR_CNFMDT IS NULL
   AND CNTR_NO         = #{cntrNo}
   AND CNTR_SN         = #{cntrSn}
   AND SV_BIZ_HCLSF_CD = #{svBizHclsfCd}
   AND SV_BIZ_DCLSF_CD = #{svBizDclsfCd}
   AND SDING_SPP_NO    = #{sppOrdNo}
   AND SDING_SPP_SN    = #{sppPlanSn}

mapper.select:
SELECT 1
  FROM TB_SVPD_SDING_SPP_CNFM_IZ   /* 모종배송확정내역 */
 WHERE CNTR_NO         = #{cntrNo}
   AND CNTR_SN         = #{cntrSn}
   AND SV_BIZ_HCLSF_CD = #{svBizHclsfCd}
   AND SV_BIZ_DCLSF_CD = #{svBizDclsfCd}
   AND SDING_SPP_NO    = #{sppOrdNo}
   AND SDING_SPP_SN    = #{sppPlanSn}
   AND ROWNUM          = 1

mapper.insert:
