UPDATE TB_SVPD_SDING_SPP_PLAN_IZ   /* 모종배송계획내역 */
   SET WK_DT         = TO_CHAR(SYSDATE, 'YYYYMMDD')   /* 작업일자 */
     , OG_TP_CD      = #{session.ogTpCd}              /* 조직유형코드 */
