) SELECT D1.CST_SV_ASN_NO /* 고객서비스배정번호 */ , ( SELECT NVL(MAX(WK_OSTR_SN), 0) FROM TB_SVST_SV_WK_OSTR_IZ WHERE CST_SV_ASN_NO = D1.CST_SV_ASN_NO ) + ROWNUM /* 작업출고일련번호 */ , D1.CNTR_NO /* 계약번호 */ , D1.CNTR_SN /* 계약일련번호 */ , '2' /* 서비스업무대분류코드 */ , D1.SV_BIZ_DCLSF_CD /* 서비스업무세분류코드 */ , #{pdCd} /* 품목상품코드 - 새싹재배기 */ , '1' /* 유무상구분코드 - 무상 */ , #{pdCd} /* 최초상품코드 - 새싹재배기 */ , D2.SELL_TP_CD /* 최초판매유형코드 */ , D3.SV_PD_TP_CD /* 최초상품용도코드 */ , NVL(D2.PD_GD_CD, 'A') /* 최초품목등급코드 */ , TO_CHAR(SYSDATE, 'YYYYMMDD') /* 최초방문완료일자 */ , #{pdCd} /* 최종상품코드 - 새싹재배기 */ , D2.SELL_TP_CD /* 최종판매유형코드 */ , D3.SV_PD_TP_CD /* 최종상품용도코드 */ , NVL(D2.PD_GD_CD, 'A') /* 최종품목등급코드 */ , TO_CHAR(SYSDATE, 'YYYYMMDD') /* 최종방문완료일자 */ , 1 /* 사용수량 */ , 0 /* 소비자단가금액 */ , 2 /* 무료체험일수 */ , TO_CHAR(SYSDATE, 'YYYYMMDD') /* 무료체험설치일자 */ , 0 /* 품목청구금액 */ , 0 /* 서비스청구금액 */ , #{mngrDvCd} /* 관리자구분코드 */ , '71314' /* 1차레벨조직ID */ , '99992' /* 2차레벨조직ID */ , '99992' /* 지점조직ID */ , #{session.employeeIDNumber} /* 담당파트너번호 */ , #{session.ogTpCd} /* 조직유형코드 */ , '100009' /* 작업창고번호 */ , 'N' /* 스티커출력여부 */ , 'N' /* 반품수불처리여부 */ , 'N' /* 반품확인여부 */ , 'N' /* 리퍼AS접수여부 */ , 'N' /* 데이터삭제여부 */
