mapper.select:
SELECT VST_DT /* 방문일자 */ , F_CMZ_CD_NM('TNT_WELLS', 'SV_BIZ_HCLSF_CD', SV_BIZ_HCLSF_CD, #{session.langId}) AS SV_BIZ_HCLSF_NM /* 업무유형 */ , F_CMZ_CD_NM('TNT_WELLS', 'PKG_DV_CD', SDING_PKG_GRP_CD, #{session.langId}) AS SDING_PKG_NM /* 패키지명 */ , LIM_QTY /* 한도수량 */ , SDING_PKG_GRP_CD /* 모종패키지그룹코드 */ , SV_BIZ_HCLSF_CD /* 서비스업무대분류코드 */ , LIM_SN /* 한도일련번호 */ FROM TB_SVPD_DCBY_SDING_RCP_LIM_IZ /* 일별모종접수한도내역 */ WHERE DTA_DL_YN = 'N' AND SV_BIZ_HCLSF_CD IN ('1', '2', '3') /* 설치, B/S, A/S */ AND VST_DT BETWEEN #{strtDt} AND #{endDt}
AND SV_BIZ_HCLSF_CD = #{svBizHclsfCd}
ORDER BY VST_DT, SV_BIZ_HCLSF_CD, SDING_PKG_GRP_CD, LIM_QTY, LIM_SN

mapper.update:
UPDATE TB_SVPD_DCBY_SDING_RCP_LIM_IZ   /* 일별모종접수한도내역 */
   SET LIM_QTY = #{limQty}   /* 한도수량 */

 WHERE DTA_DL_YN        = 'N'
   AND VST_DT           = #{vstDt}
   AND SDING_PKG_GRP_CD = #{sdingPkgGrpCd}
   AND SV_BIZ_HCLSF_CD  = #{svBizHclsfCd}
   AND LIM_SN           = #{limSn}

mapper.insert:
INSERT INTO TB_SVPD_DCBY_SDING_RCP_LIM_IZ   /* 일별모종접수한도내역 */
     (
       VST_DT             /* 방문일자 */
     , SDING_PKG_GRP_CD   /* 모종패키지그룹코드 */
     , SV_BIZ_HCLSF_CD    /* 서비스업무대분류코드 */
     , LIM_SN             /* 한도일련번호 */
     , LIM_QTY            /* 한도수량 */
     , DTA_DL_YN          /* 데이터삭제여부 */

     )
VALUES
     (
       REPLACE(#{vstDt}, '-', '')
     , #{sdingPkgGrpCd}
     , #{svBizHclsfCd}
     , ( SELECT NVL(MAX(LIM_SN), 0) + 1
           FROM TB_SVPD_DCBY_SDING_RCP_LIM_IZ
          WHERE VST_DT           = #{vstDt}
            AND SDING_PKG_GRP_CD = #{sdingPkgGrpCd}
            AND SV_BIZ_HCLSF_CD  = #{svBizHclsfCd} )
     , #{limQty}
     , 'N'

     )
