mapper.insert:
MERGE INTO TB_SVPD_SDING_PKG_QTY_CTR_IZ TB_TGT /* 모종패키지수량조정내역 */ USING ( SELECT #{dgGgLctCd} AS DG_GG_LCT_CD /* 대표집하위치코드 */ , #{sdingPkgCd} AS SDING_PKG_CD /* 모종패키지코드 */ , #{ostrDuedt} AS OSTR_DUEDT /* 출고예정일자 */ , #{excdQty} AS EXCD_QTY /* 제외수량 */ , #{spmtQty} AS SPMT_QTY /* 추가수량 */ FROM DUAL ) TB_SRC ON ( TB_TGT.DG_GG_LCT_CD = TB_SRC.DG_GG_LCT_CD AND TB_TGT.SDING_PKG_CD = TB_SRC.SDING_PKG_CD AND TB_TGT.OSTR_DUEDT = TB_SRC.OSTR_DUEDT ) WHEN MATCHED THEN UPDATE SET TB_TGT.EXCD_QTY = TB_SRC.EXCD_QTY , TB_TGT.SPMT_QTY = TB_SRC.SPMT_QTY
WHEN NOT MATCHED THEN INSERT ( TB_TGT.DG_GG_LCT_CD /* 대표집하위치코드 */ , TB_TGT.SDING_PKG_CD /* 모종패키지코드 */ , TB_TGT.OSTR_DUEDT /* 출고예정일자 */ , TB_TGT.EXCD_QTY /* 제외수량 */ , TB_TGT.SPMT_QTY /* 추가수량 */ , TB_TGT.DTA_DL_YN /* 데이터삭제여부 */
) VALUES ( TB_SRC.DG_GG_LCT_CD /* 대표집하위치코드 */ , TB_SRC.SDING_PKG_CD /* 모종패키지코드 */ , TB_SRC.OSTR_DUEDT /* 출고예정일자 */ , TB_SRC.EXCD_QTY /* 제외수량 */ , TB_SRC.SPMT_QTY /* 추가수량 */ , 'N' /* 데이터삭제여부 */
)
