mapper.select:
SELECT T1.WARE_NO /* 창고번호 */ , T2.WARE_NM /* 창고명 */ , T2.WARE_DV_CD /* 창고구분코드 */ , T5.OG_NM /* 조직명 */ , T2.WARE_MNGT_PRTNR_NO /* 창고관리파트너번호 */ , T3.SVPD_ITEM_KND AS ITEM_KND /* 품목구분 */ , T1.CTR_WK_DT /* 조정작업일자 */ , T1.STAT_CTR_APY_DT /* 상태조정일자 */ , TO_CHAR(TO_NUMBER(T3.SVPD_SAP_CD)) AS SAP_CD /* SAP코드 */ , T1.PD_CD AS ITM_PD_CD /* 품목코드 */ , T3.SVPD_NM_ABBR1 AS ITM_PD_NM /* 품목명 */ , T3.SVPD_MGT_UNT AS MGT_UNIT /* 관리단위 */ , T1.ITM_GD_CTR_TP_CD /* 품목등급조정유형코드 */
, F_CMZ_CD_NM('TNT_WELLS', 'ITM_GD_CTR_TP_CD', T1.ITM_GD_CTR_TP_CD, 'ko') AS ITM_GD_CTR_TP_NM , F_CMZ_CD_NM('TNT_WELLS', 'MAT_STOC_STAT_CTR_CD', T1.ITM_GD_CTR_TP_CD, 'ko') AS ITM_GD_CTR_TP_NM
, NVL(T1.BFCT_NOM_STOC_A_GD_QTY, 0) AS BFCT_NOM_STOC_A_GD_QTY/*조정전정상재고A등급수량*/ , NVL(T1.BFCT_NOM_STOC_E_GD_QTY, 0) AS BFCT_NOM_STOC_E_GD_QTY/*조정전정상재고E등급수량*/ , NVL(T1.BFCT_NOM_STOC_R_GD_QTY, 0) AS BFCT_NOM_STOC_R_GD_QTY/*조정전정상재고R등급수량*/ , T1.BFCT_ITM_GD_CD /*조정전품목등급코드*/ , T1.AFCT_ITM_GD_CD /*조정후품목등급코드*/ , NVL(T1.CTR_QTY, 0) AS CTR_QTY/*조정수량*/ , F_CMZ_CD_NM('TNT_WELLS', 'CTR_RSON_CD', T1.ITM_GD_CTR_RSON_CD, 'ko') AS ITM_GD_CTR_RSON_NM /*조정사유명*/ , T1.ITM_GD_CTR_RSON_CD /* 품목등급조정사유코드 */ , T1.CTR_SN /*조정일련번호*/ , T1.RMK_CN /*비고*/ FROM TB_SVST_ITM_GD_CTR_IZ T1 /* 품목등급조정내역 */ INNER JOIN TB_SVST_MCBY_WARE_IZ T2 /* 월별창고내역 */ ON T2.WARE_NO = T1.WARE_NO AND T2.APY_YM = SUBSTR(T1.STAT_CTR_APY_DT, 1,6) INNER JOIN () T3 ON T3.SVPD_PD_CD = T1.PD_CD LEFT OUTER JOIN TB_OGBS_MM_PRTNR_IZ T4 /* 월별파트너내역 */ ON T4.BASE_YM = TO_CHAR(SYSDATE, 'YYYYMM') AND T4.PRTNR_NO = T1.WARE_ICHR_PRTNR_NO AND T4.OG_TP_CD = T1.OG_TP_CD LEFT OUTER JOIN TB_OGBS_MM_OG_IZ T5 /* 월별조직내역 */ ON T5.BASE_YM = T4.BASE_YM AND T5.OG_ID = T4.OG_ID AND T5.OG_TP_CD = T4.OG_TP_CD WHERE T1.STAT_CTR_APY_DT BETWEEN #{stFromYmd} AND #{edToYmd} AND T1.WARE_NO = #{wareNo} AND T3.SVPD_ITEM_KND = #{itmKnd}
AND T1.ITM_GD_CTR_TP_CD = #{itmGdCtrTpCd}
AND T1.ITM_GD_CTR_RSON_CD = #{itmGdCtrRsonCd}
AND T1.DTA_DL_YN = 'N'

mapper.update:
UPDATE TB_SVST_ITM_GD_CTR_IZ    /* 품목등급조정내역 */
   SET DTA_DL_YN = 'Y'

 WHERE ITM_GD_CTR_TP_CD = #{itmGdCtrTpCd}
   AND WARE_NO = #{wareNo}
   AND CTR_WK_DT = #{ctrWkDt}
   AND CTR_SN = #{ctrSn}
