mapper.select:
SELECT D1.STR_RGST_DT /* 입고등록일자 */ , D1.ITM_PD_CD /* 품목상품코드 */ , TO_CHAR(TO_NUMBER(D2.SAP_MAT_CD)) AS SAP_MAT_CD /* 품목SAP코드 */ , D2.PD_ABBR_NM /* 품목명 */ , F_CMZ_CD_NM('TNT_WELLS', 'STR_TP_CD', D1.STR_TP_CD) AS STR_TP_NM /* 입고코드명 */ , F_CMZ_CD_NM('TNT_WELLS', 'MNGT_UNIT_CD', D1.MNGT_UNIT_CD) AS MNGT_UNIT_NM /* 관리단위코드명 */ , F_CMZ_CD_NM('TNT_WELLS', 'PD_GD_CD', D1.ITM_GD_CD) AS ITM_GD_NM /* 품목등급코드명 */ , D1.STR_QTY /* 입고수량 */ , D1.OSTR_DT /* 출고일자 */ , D1.OSTR_WARE_NO /* 출고창고번호 */ , D1.STR_WARE_NO /* 입고창고번호 */ , D3.WARE_NM AS STR_WARE_NM /* 입고창고명 */ , D1.ITM_STR_NO /* 품목입고번호 */ , D1.ITM_OSTR_NO /* 품목출고번호 */ , D3.HGR_WARE_NO AS STR_WARE_NO_UP /* 입고상위창고번호 */ , D3.WARE_DTL_DV_CD AS STR_WARE_DTL_DV_CD /* 입고창고상세구분 */ , F_CMZ_CD_NM('TNT_WELLS', 'WARE_DTL_DV_CD', D3.WARE_DTL_DV_CD) AS STR_WARE_DTL_DV_NM /* 입고창고상세구분명 */ , D4.WARE_NM AS OSTR_WARE_NM /* 출고창고명 */ , D4.HGR_WARE_NO AS OSTR_WARE_NO_UP /* 출고상위창고번호 */ , D4.WARE_DTL_DV_CD AS OSTR_WARE_DTL_DV_CD /* 출고창고상세구분 */ , F_CMZ_CD_NM('TNT_WELLS', 'WARE_DTL_DV_CD', D4.WARE_DTL_DV_CD) AS OSTR_WARE_DTL_DV_NM /* 출고창고상세구분명 */ FROM TB_SVST_ITM_STR_IZ D1 /* 품목입고내역 */ INNER JOIN TB_PDBS_PD_BAS D2 /* 상품기본 */ ON D2.PD_CD = D1.ITM_PD_CD LEFT OUTER JOIN TB_SVST_MCBY_WARE_IZ D3 /* 월별창고내역(입고) */ ON D3.APY_YM = SUBSTR(D1.STR_RGST_DT,1,6) AND D3.WARE_NO = D1.STR_WARE_NO AND D3.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_SVST_MCBY_WARE_IZ D4 /* 월별창고내역(출고) */ ON D4.APY_YM = SUBSTR(D1.OSTR_DT,1,6) AND D4.WARE_NO = D1.OSTR_WARE_NO AND D4.DTA_DL_YN = 'N'
LEFT OUTER JOIN TB_PDBS_PD_ECOM_PRP_DTL D5 /* 상품각사속성상세 */ ON D5.PD_CD = D1.ITM_PD_CD AND D5.DTA_DL_YN = 'N' AND D5.PD_EXTS_PRP_GRP_CD = 'PART'
INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL D5 /* 상품각사속성상세 */ ON D5.PD_CD = D1.ITM_PD_CD AND D5.DTA_DL_YN = 'N' AND D5.PD_EXTS_PRP_GRP_CD = 'PART'
WHERE D1.DTA_DL_YN = 'N' AND D2.DTA_DL_YN = 'N' AND D2.PD_TP_CD = 'M' AND D1.STR_RGST_DT BETWEEN #{stStrDt} AND #{edStrDt} AND D1.STR_WARE_NO LIKE #{strWareDvCd} || '%'
AND D4.WARE_DV_CD = #{ostrWareDvCd}
AND ( D3.HGR_WARE_NO = #{strWareNoM} OR D3.WARE_NO = #{strWareNoM} )
AND D3.WARE_NO = #{strWareNoD}
AND D3.WARE_DTL_DV_CD = #{strWareDtlDvCd}
AND ( D4.HGR_WARE_NO = #{ostrWareNoM} OR D4.WARE_NO = #{ostrWareNoM} )
AND D4.WARE_NO = #{ostrWareNoD}
AND D4.WARE_DTL_DV_CD = #{ostrWareDtlDvCd}
AND D1.STR_TP_CD = #{strTpCd}
AND D5.PD_PRP_VAL19 = #{itmKndCd}
AND D1.ITM_PD_CD = #{itmPdCd}
AND D1.ITM_GD_CD = #{pgGdCd}
AND ( D5.PD_PRP_VAL32 IS NULL OR D5.PD_PRP_VAL32 = 'Y' )
AND D5.PD_PRP_VAL32 <> 'Y'
ORDER BY D1.STR_RGST_DT, D1.ITM_PD_CD
