mapper.insert:
INSERT INTO TB_SVST_MCBY_WARE_IZ ( /*월병창고내역*/ APY_YM /* 적용년월 */ , WARE_NO /* 창고번호 */ , WARE_DV_CD /* 창고구분코드 */ , WARE_DTL_DV_CD /* 창고상세구분코드 */ , WARE_LOCARA_CD /* 창고지역코드 */ , WARE_LOCARA_SN /* 창고지역일련번호 */ , HGR_WARE_NO /* 상위창고번호 */ , WARE_NM /* 창고명 */ , WARE_ICHR_NO /* 창고담당번호 */ , WARE_MNGT_PRTNR_NO /* 창고관리파트너번호 */ , OG_TP_CD /* 조직유형코드 */ , OG_ID /* 조직ID */ , CO_CD /* 회사코드 */ , DIDY_DV_CD /* 직배구분코드 */ , SORT_DV_VAL /* 정렬구분값 */ , WARE_ADR_ID /* 창고주소ID */ , BLD_CD /* 빌딩코드 */ , WARE_USE_YN /* 창고사용여부 */ , ADR_USE_YN /* 주소사용여부 */ , STD_WARE_USE_YN /* 표준창고사용여부 */ , AUTO_APLC_EXCD_YN /* 자동신청제외여부 */ , APY_STRTDT /* 적용시작일자 */ , APY_ENDDT /* 적용종료일자 */ , RMK_CN /* 비고내용 */ , DTA_DL_YN /* 데이터삭제여부 */
) SELECT TO_CHAR(ADD_MONTHS(TO_DATE(APY_YM,'YYYYMM'),1),'YYYYMM') , WARE_NO , WARE_DV_CD , WARE_DTL_DV_CD , WARE_LOCARA_CD , WARE_LOCARA_SN , HGR_WARE_NO , WARE_NM , WARE_ICHR_NO , WARE_MNGT_PRTNR_NO , OG_TP_CD /* 조직유형코드 */ , OG_ID /* 조직ID */ , CO_CD , DIDY_DV_CD , SORT_DV_VAL , WARE_ADR_ID , BLD_CD , WARE_USE_YN , ADR_USE_YN , STD_WARE_USE_YN , AUTO_APLC_EXCD_YN , APY_STRTDT , APY_ENDDT , RMK_CN /* 비고내용 */ , DTA_DL_YN /* 데이터삭제여부 */
FROM TB_SVST_MCBY_WARE_IZ WHERE APY_YM=TO_CHAR(TO_DATE(#{baseYm},'YYYYMM'),'YYYYMM')

mapper.select:
SELECT B.APY_YM AS APY_YM /* 적용년월 */ , B.WARE_DV_CD AS WARE_DV_CD /* 창고구분코드 */ , B.WARE_DV_CD ||B.WARE_LOCARA_CD||B.WARE_ICHR_NO AS WARE_CD /*창고코드*/ , A.WARE_DV_CD ||A.WARE_LOCARA_CD||A.WARE_ICHR_NO AS HGR_WARE /*상위창고코드*/ , B.WARE_DTL_DV_CD AS WARE_DTL_DV_CD /*창고상세구분코드*/ , B.WARE_LOCARA_CD AS WARE_LOCARA_CD /*창고지역코드*/ , B.WARE_LOCARA_SN AS WARE_LOCARA_SN /*창고지역순번*/ , B.HGR_WARE_NO AS HGR_WARE_NO /*상위창고번호*/ , A.WARE_NM AS HGR_WARE_NM /*상위창고명*/ , B.WARE_NO AS WARE_NO /*창고번호*/ , B.WARE_NM AS WARE_NM /*창고명*/ , B.WARE_MNGT_PRTNR_NO AS WARE_MNGT_PRTNR_NO /*파트너번호*/ , B.DIDY_DV_CD AS DIDY_DV_CD /*직배구분코드*/ , B.WARE_ADR_ID AS WARE_ADR_ID /*창고주소*/ , B.WARE_USE_YN AS WARE_USE_YN /*창고사용여부*/ , B.ADR_USE_YN AS ADR_USE_YN /*지정주소사용여부*/ , C.RNADR || ' ' || C.RDADR AS ADR_NM , (SELECT A.BLD_NM FROM TB_OGBS_BLD_BAS A WHERE A.BLD_CD = B.BLD_CD) AS DSN_BLD_NM /*지정빌딩명*/ , ( SELECT PRTNR_KNM FROM TB_OGBS_MM_PRTNR_IZ A WHERE A.PRTNR_NO = B.WARE_MNGT_PRTNR_NO AND A.BASE_YM = B.APY_YM AND A.OG_TP_CD IN ('W02', 'W06') AND ROWNUM = 1) AS WARE_STOC_MGR /*파트너담당자명*/ , (SELECT A.BLD_NM FROM TB_OGBS_MM_OG_IZ A , TB_OGBS_MM_PRTNR_IZ C WHERE A.OG_ID = C.OG_ID AND A.BASE_YM = C.BASE_YM AND C.OG_TP_CD IN ('W02','W06') AND C.PRTNR_NO = B.WARE_MNGT_PRTNR_NO AND C.BASE_YM = B.APY_YM ) AS BLD_NM /*빌딩명*/
FROM TB_SVST_MCBY_WARE_IZ B LEFT OUTER JOIN TB_SVST_MCBY_WARE_IZ A ON B.HGR_WARE_NO = A.WARE_NO AND B.APY_YM = A.APY_YM LEFT OUTER JOIN TB_GBCO_ADR_BAS C ON C.ADR_ID = B.WARE_ADR_ID
WHERE 1 = 1 AND B.APY_YM = #{baseYm}
AND B.WARE_DTL_DV_CD = #{wareDtlDvCd}
AND B.WARE_MNGT_PRTNR_NO = #{wareMngtPrtnrNo}
AND B.WARE_DV_CD = #{wareDvCd}
AND (B.HGR_WARE_NO = #{wareNoM} or B.WARE_NO = #{wareNoM})
AND B.WARE_NO = #{wareNoD}
AND B.WARE_USE_YN = #{codeUseYn}
/*AND B.WARE_NO = '305821'*/ ORDER BY B.WARE_DV_CD, B.HGR_WARE_NO, B.WARE_NM, B.WARE_LOCARA_CD, B.WARE_LOCARA_SN, B.WARE_DTL_DV_CD

SELECT COUNT(T1.WARE_NO) FROM TB_SVST_MCBY_WARE_IZ T1 /* 월별창고내역 */ WHERE 1 = 1 AND T1.APY_YM = #{apyYm}
AND T1.WARE_NO != #{wareNo}
AND T1.WARE_DTL_DV_CD IN ('20', '30') /* 조직창고인 경우 */ AND T1.WARE_ICHR_NO = '000' /* TODO: 추후 컬럼 삭제 예정 */
AND T1.WARE_DTL_DV_CD IN ('21', '31', '32') /* 조직창고가 아닌 경우 */ AND T1.WARE_ICHR_NO != '000' /* TODO: 추후 컬럼 삭제 예정 */
AND T1.WARE_USE_YN = 'Y' AND TRIM(T1.WARE_MNGT_PRTNR_NO) = TRIM(#{prtnrNo})

mapper.insert:
INSERT INTO TB_SVST_MCBY_WARE_IZ (  /* 월별창고내역 */
      APY_YM             /* 적용년월 */
    , WARE_NO            /* 창고번호 */
    , WARE_DV_CD         /* 창고구분코드 */
    , WARE_DTL_DV_CD     /* 창고상세구분코드 */
    , HGR_WARE_NO        /* 상위창고번호 */
    , WARE_NM            /* 창고명 */
    , WARE_ICHR_NO       /* 창고담당번호 */
    , WARE_MNGT_PRTNR_NO /* 창고관리파트너번호 */
    , OG_TP_CD           /* 조직유형코드 */
    , OG_ID              /* 조직ID */
    , DIDY_DV_CD         /* 직배구분코드 */
    , SORT_DV_VAL        /* 정렬구분값 */
    , WARE_ADR_ID        /* 창고주소ID */
    , BLD_CD             /* 빌딩코드 */
    , WARE_USE_YN        /* 창고사용여부 */
    , ADR_USE_YN         /* 주소사용여부 */
    , APY_STRTDT         /* 적용시작일자 */
    , APY_ENDDT          /* 적용종료일자 */
    , RMK_CN             /* 비고내용 */
    , DTA_DL_YN          /* 데이터삭제여부 */
    )
SELECT #{apyYm}
    , (SELECT #{wareDvCd} || LPAD(NVL(TO_NUMBER(MAX(SUBSTR(WARE_NO, 2))), 0) + 1, 5, '0')
         FROM TB_SVST_MCBY_WARE_IZ
        WHERE APY_YM = #{apyYm}
          AND WARE_DV_CD = #{wareDvCd})
    , #{wareDvCd}
    , #{wareDtlDvCd}
    , #{hgrWareNo}
    , #{wareNm}
    , (CASE WHEN #{wareDtlDvCd} IN ('20', '30') THEN '000' ELSE '010' END) /* 창고담당번호 */
    , #{prtnrNo}
    , #{ogTpCd}
    , #{ogId}
    , (CASE WHEN #{wareDtlDvCd} = '32' THEN '2' ELSE '1' END) /* 직배구분코드 */
    , #{sortDvVal}
    , #{wareAdrId}
    , (CASE WHEN #{adrUseYn} = 'Y' THEN #{bldCd} ELSE NULL END) /* 빌딩코드 */
    , 'Y'
    , #{adrUseYn}
    , TO_CHAR(SYSDATE, 'YYYYMMDD')
    , '99991231'
    , #{rmkCn}
    , 'N'

 FROM DUAL

mapper.update:
UPDATE TB_SVST_MCBY_WARE_IZ /* 월별창고내역 */
   SET WARE_DTL_DV_CD = #{wareDtlDvCd}
     , WARE_ICHR_NO = (CASE WHEN #{wareDtlDvCd} IN ('20', '30') THEN '000' ELSE '010' END)
     , DIDY_DV_CD = (CASE WHEN #{wareDtlDvCd} = '32' THEN '2' ELSE '1' END)
     , HGR_WARE_NO = #{hgrWareNo}
     , SORT_DV_VAL = #{sortDvVal}
     , WARE_ADR_ID = #{wareAdrId}
     , BLD_CD = (CASE WHEN #{adrUseYn} = 'Y' THEN #{bldCd} ELSE NULL END)
     , WARE_USE_YN = #{wareUseYn}
     , ADR_USE_YN = #{adrUseYn}
     , RMK_CN = #{rmkCn}
     , WARE_NM = #{wareNm}

 WHERE 1 = 1
   AND APY_YM = #{apyYm}
   AND WARE_NO = #{wareNo}

mapper.select:
SELECT DISTINCT T2.BLD_CD , T2.BLD_NM , T2.BLD_NM || '(' || T2.BLD_CD || ')' AS BLD_CD_NM , T3.RNADR || ' ' || T3.RDADR AS BLD_ADR , T3.RNADR , T3.RDADR , T3.NEW_ADR_ZIP , T2.ADR_ID FROM TB_OGBS_MM_OG_IZ T1 INNER JOIN TB_OGBS_BLD_BAS T2 ON T1.OG_TP_CD = T2.OG_TP_CD AND T1.BLD_CD = T2.BLD_CD LEFT OUTER JOIN TB_GBCO_ADR_BAS T3 ON T3.ADR_ID = T2.ADR_ID WHERE T1.BASE_YM = TO_CHAR(SYSDATE, 'YYYYMM') AND T2.DTA_DL_YN = 'N' AND T1.DTA_DL_YN = 'N' AND T2.OG_TP_CD IN ('W02', 'ALC')
AND T1.DGR1_LEVL_OG_ID = #{dgr1LevlOgId}
AND T1.DGR2_LEVL_OG_ID = #{dgr2LevlOgId}
ORDER BY T2.BLD_CD
