mapper.insert:
INSERT INTO TB_SVPD_BFSVC_NX_VST_PROM_IZ (
       CNTR_NO
     , CNTR_SN
     , SAVE_SN
     , OG_TP_CD
     , PRTNR_NO
     , VST_PROM_DT
     , CSTMW_FILT_PD_CD
     , CONF_YN
     , DTA_DL_YN

) VALUES (
       #{cntrNo}
     , #{cntrSn}
     , NVL((SELECT MAX(SAVE_SN)
              FROM TB_SVPD_BFSVC_NX_VST_PROM_IZ
             WHERE CNTR_NO = #{cntrNo}
               AND CNTR_SN = #{cntrSn}
               AND DTA_DL_YN = 'N'), 0) + 1
     , #{session.ogTpCd}
     , #{session.employeeIDNumber}
     , #{vstPromDt}
     , #{partPdCdAfter}
     , 'N'
     , 'N'

)

mapper.update:
UPDATE TB_SVPD_CST_SV_RGBSPR_IZ
   SET PART_PD_CD = #{partPdCdAfter}

 WHERE CNTR_NO = #{cntrNo}
   AND CNTR_SN = #{cntrSn}
   AND PART_PD_CD = #{partPdCdBefore}
   AND VST_DUEDT = #{vstPromDt}
   AND DTA_DL_YN = 'N'
   AND WK_DT IS NULL
