SELECT NM , CASE WHEN NM = '4' THEN NVL(ACOL1,0) ELSE NVL(ACOL1,0) END AS ACOL1 , CASE WHEN NM = '4' THEN NVL(ACOL2,0) ELSE NVL(ACOL2,0) END AS ACOL2 , CASE WHEN NM = '4' THEN NVL(ACOL3,0) ELSE NVL(ACOL3,0) END AS ACOL3 , CASE WHEN NM = '4' THEN NVL(ACOL4,0) ELSE NVL(ACOL4,0) END AS ACOL4 , CASE WHEN NM = '4' THEN NVL(ACOL5,0) ELSE NVL(ACOL5,0) END AS ACOL5 , CASE WHEN NM = '4' THEN NVL(ACOL6,0) ELSE NVL(ACOL6,0) END AS ACOL6 , CASE WHEN NM = '4' THEN NVL(ACOL7,0) ELSE NVL(ACOL7,0) END AS ACOL7 , CASE WHEN NM = '4' THEN NVL(ACOL8,0) ELSE NVL(ACOL8,0) END AS ACOL8 , CASE WHEN NM = '4' THEN NVL(ACOL9,0) ELSE NVL(ACOL9,0) END AS ACOL9 , CASE WHEN NM = '4' THEN NVL(ACOL10,0) ELSE NVL(ACOL10,0) END AS ACOL10 , CASE WHEN NM = '4' THEN NVL(ACOL11,0) ELSE NVL(ACOL11,0) END AS ACOL11 , CASE WHEN NM = '4' THEN NVL(ACOL12,0) ELSE NVL(ACOL12,0) END AS ACOL12 , 0 AS MAXVAL , 0 AS MINVAL , CASE WHEN NM = '4' THEN NVL(ACOL1+ACOL2+ACOL3+ACOL4+ACOL5+ACOL6+ACOL7+ACOL8+ACOL9+ACOL10+ACOL11+ACOL12,0) ELSE NVL(ACOL1+ACOL2+ACOL3+ACOL4+ACOL5+ACOL6+ACOL7+ACOL8+ACOL9+ACOL10+ACOL11+ACOL12,0) END as TCNT , 0 AS AVG FROM (
SELECT '1' AS NM -- 당월 기준 설치 1년 이내 제품불만 A/S 누적처리건수 , (NVL(SUM(CASE WHEN ((INSTDT >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'01','YYYYMM'),-4),'YYYYMM') AND INSTDT <= #{baseY}||'01') AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'01','YYYYMM'),-11),'YYYYMM') AND YYYYMM <= #{baseY}||'01') AND (VS161_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= #{baseY}||'01') OR (#{baseY}||'01' > EDD AND INSTDT >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'01','YYYYMM'),-4),'YYYYMM') AND INSTDT <= EDD) AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(EDD,'YYYYMM'),-11),'YYYYMM') AND YYYYMM <= EDD) AND (VS161_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= EDD) ) THEN 1 ELSE 0 END),0)) AS ACOL1 , (NVL(SUM(CASE WHEN ((INSTDT >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'02','YYYYMM'),-4),'YYYYMM') AND INSTDT <= #{baseY}||'02') AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'02','YYYYMM'),-11),'YYYYMM') AND YYYYMM <= #{baseY}||'02') AND (VS161_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= #{baseY}||'02') OR (#{baseY}||'02' > EDD AND INSTDT >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'01','YYYYMM'),-4),'YYYYMM') AND INSTDT <= EDD) AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(EDD,'YYYYMM'),-11),'YYYYMM') AND YYYYMM <= EDD) AND (VS161_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= EDD) ) THEN 1 ELSE 0 END),0)) AS ACOL2 , (NVL(SUM(CASE WHEN ((INSTDT >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'03','YYYYMM'),-4),'YYYYMM') AND INSTDT <= #{baseY}||'03') AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'03','YYYYMM'),-11),'YYYYMM') AND YYYYMM <= #{baseY}||'03') AND (VS161_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= #{baseY}||'03') OR (#{baseY}||'03' > EDD AND INSTDT >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'01','YYYYMM'),-4),'YYYYMM') AND INSTDT <= EDD) AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(EDD,'YYYYMM'),-11),'YYYYMM') AND YYYYMM <= EDD) AND (VS161_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= EDD) ) THEN 1 ELSE 0 END),0)) AS ACOL3 , (NVL(SUM(CASE WHEN ((INSTDT >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'04','YYYYMM'),-4),'YYYYMM') AND INSTDT <= #{baseY}||'04') AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'04','YYYYMM'),-11),'YYYYMM') AND YYYYMM <= #{baseY}||'04') AND (VS161_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= #{baseY}||'04') OR (#{baseY}||'04' > EDD AND INSTDT >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'01','YYYYMM'),-4),'YYYYMM') AND INSTDT <= EDD) AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(EDD,'YYYYMM'),-11),'YYYYMM') AND YYYYMM <= EDD) AND (VS161_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= EDD) ) THEN 1 ELSE 0 END),0)) AS ACOL4 , (NVL(SUM(CASE WHEN ((INSTDT >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'05','YYYYMM'),-4),'YYYYMM') AND INSTDT <= #{baseY}||'05') AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'05','YYYYMM'),-11),'YYYYMM') AND YYYYMM <= #{baseY}||'05') AND (VS161_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= #{baseY}||'05') OR (#{baseY}||'05' > EDD AND INSTDT >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'01','YYYYMM'),-4),'YYYYMM') AND INSTDT <= EDD) AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(EDD,'YYYYMM'),-11),'YYYYMM') AND YYYYMM <= EDD) AND (VS161_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= EDD) ) THEN 1 ELSE 0 END),0)) AS ACOL5 , (NVL(SUM(CASE WHEN ((INSTDT >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'06','YYYYMM'),-4),'YYYYMM') AND INSTDT <= #{baseY}||'06') AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'06','YYYYMM'),-11),'YYYYMM') AND YYYYMM <= #{baseY}||'06') AND (VS161_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= #{baseY}||'06') OR (#{baseY}||'06' > EDD AND INSTDT >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'01','YYYYMM'),-4),'YYYYMM') AND INSTDT <= EDD) AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(EDD,'YYYYMM'),-11),'YYYYMM') AND YYYYMM <= EDD) AND (VS161_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= EDD) ) THEN 1 ELSE 0 END),0)) AS ACOL6 , (NVL(SUM(CASE WHEN ((INSTDT >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'07','YYYYMM'),-4),'YYYYMM') AND INSTDT <= #{baseY}||'07') AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'07','YYYYMM'),-11),'YYYYMM') AND YYYYMM <= #{baseY}||'07') AND (VS161_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= #{baseY}||'07') OR (#{baseY}||'07' > EDD AND INSTDT >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'01','YYYYMM'),-4),'YYYYMM') AND INSTDT <= EDD) AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(EDD,'YYYYMM'),-11),'YYYYMM') AND YYYYMM <= EDD) AND (VS161_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= EDD) ) THEN 1 ELSE 0 END),0)) AS ACOL7 , (NVL(SUM(CASE WHEN ((INSTDT >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'08','YYYYMM'),-4),'YYYYMM') AND INSTDT <= #{baseY}||'08') AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'08','YYYYMM'),-11),'YYYYMM') AND YYYYMM <= #{baseY}||'08') AND (VS161_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= #{baseY}||'08') OR (#{baseY}||'08' > EDD AND INSTDT >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'01','YYYYMM'),-4),'YYYYMM') AND INSTDT <= EDD) AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(EDD,'YYYYMM'),-11),'YYYYMM') AND YYYYMM <= EDD) AND (VS161_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= EDD) ) THEN 1 ELSE 0 END),0)) AS ACOL8 , (NVL(SUM(CASE WHEN ((INSTDT >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'09','YYYYMM'),-4),'YYYYMM') AND INSTDT <= #{baseY}||'09') AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'09','YYYYMM'),-11),'YYYYMM') AND YYYYMM <= #{baseY}||'09') AND (VS161_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= #{baseY}||'09') OR (#{baseY}||'09' > EDD AND IN