LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= #{baseY}||'12') OR (#{baseY}||'12' > EDD AND INSTDT >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'01','YYYYMM'),-4),'YYYYMM') AND INSTDT <= EDD) AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(EDD,'YYYYMM'),-11),'YYYYMM') AND YYYYMM <= EDD) AND (VS161_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= EDD) ) THEN 1 ELSE 0 END),0)) AS ACOL12
FROM ( SELECT /*+ use_hash(VS161 AC201 CYC_TP) */ SUBSTR(VS161.SV_BIZ_DCLSF_CD,0,2) AS WRK_TYP , VS161.WK_PROCS_YM AS YYYYMM , WK_PRCSDT AS VS161_WRK_DT , SUBSTR(VS161.IST_DT, 1, 6) AS INSTDT , VS161.CST_SV_ASN_NO , ST108.LNC_STRTDT AS ST108_LAUN_ST , ST108.LNC_EXDT AS ST108_LAUN_ED , (SELECT MAX(SUBSTR(ST108.LNC_EXDT,1,6)) FROM TB_SVST_NWPRD_M3_LNC_IZ SUB1 WHERE SUB1.LNC_EXDT LIKE #{baseY}||'%' AND SUB1.PD_CD = VS161.PD_CD
AND SUB1.PD_CD = #{pdCd}
AND SUB1.PD_GRP_CD = #{pdGrp}
AND SUB1.PD_GRP_CD = #{pdGrp}
AND SUB1.PD_GRP_CD = #{pdGrp}
AND SUB1.PD_GRP_CD = #{pdGrp}
AND SUB1.PD_GRP_CD = #{pdGrp}
AND SUB1.PD_GRP_CD IN ('5','7','8','9')
) AS EDD FROM TB_SVPD_QLTY_MNGT_AS_DTL_IZ VS161 INNER JOIN WSMDBS.TB_SVPD_CST_SV_EXCN_IZ AC201 ON VS161.CNTR_NO = AC201.CNTR_NO AND VS161.CNTR_SN = AC201.CNTR_SN INNER JOIN TB_SVST_NWPRD_M3_LNC_IZ ST108 ON ST108.PD_CD = VS161.PD_CD AND ST108.USE_YN = 'Y' AND ST108.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_PDBS_PD_ECOM_PRP_DTL CYC_TP ON CYC_TP.PD_CD = AC201.SV_PD_CD AND CYC_TP.PD_EXTS_PRP_GRP_CD = 'SCHD' WHERE (VS161.WK_PRCSDT BETWEEN ST108.LNC_STRTDT AND ST108.LNC_EXDT)
AND ((VS161.WK_PROCS_YM < '201901' AND (VS161.IMPTA_RSON_CD IN ('1','2','3','4','5','6') OR VS161.IMPTA_RSON_CD IS NULL)) OR (VS161.WK_PROCS_YM >= '201901' AND (VS161.BAD_DV_CD IN ('100R','500R','400R','700R') OR VS161.BAD_DV_CD IS NULL)))
AND TRIM(VS161.PD_GRP_CD) != '11' AND ((VS161.SV_PROCS_FOM_CD != '114A') OR (VS161.SV_PROCS_FOM_CD IS NULL))
AND VS161.SV_BIZ_DCLSF_CD = #{svType}
AND VS161.BAD_DV_CD = #{badDivide}
AND VS161.PD_CD = #{pdCd}
AND VS161.PD_GRP_CD = #{pdGrp}
AND VS161.PD_GRP_CD = #{pdGrp}
AND VS161.PD_GRP_CD = #{pdGrp}
AND VS161.PD_GRP_CD = #{pdGrp}
AND VS161.PD_GRP_CD = #{pdGrp}
AND VS161.PD_GRP_CD IN ('5','7','8','9')
) UNION
SELECT '2' AS NM -- 1년(12개월)누적설치수량( 1년: 당월~11개월前설치수량의합) , (NVL(SUM(CASE WHEN ((YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'02','YYYYMM'),-12),'YYYYMM') AND YYYYMM <= #{baseY}||'01') AND (AC221_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= #{baseY}||'01')) OR ( #{baseY}||'01' > EDD AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(EDD,'YYYYMM'),-11),'YYYYMM') AND YYYYMM <= EDD) AND (AC221_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= EDD)) THEN 1 ELSE 0 END),0)) AS ACOL1 , (NVL(SUM(CASE WHEN ((YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'03','YYYYMM'),-12),'YYYYMM') AND YYYYMM <= #{baseY}||'02') AND (AC221_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= #{baseY}||'02')) OR ( #{baseY}||'02' > EDD AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(EDD,'YYYYMM'),-11),'YYYYMM') AND YYYYMM <= EDD) AND (AC221_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= EDD)) THEN 1 ELSE 0 END),0)) AS ACOL2 , (NVL(SUM(CASE WHEN ((YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'04','YYYYMM'),-12),'YYYYMM') AND YYYYMM <= #{baseY}||'03') AND (AC221_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= #{baseY}||'03')) OR ( #{baseY}||'03' > EDD AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(EDD,'YYYYMM'),-11),'YYYYMM') AND YYYYMM <= EDD) AND (AC221_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= EDD)) THEN 1 ELSE 0 END),0)) AS ACOL3 , (NVL(SUM(CASE WHEN ((YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'05','YYYYMM'),-12),'YYYYMM') AND YYYYMM <= #{baseY}||'04') AND (AC221_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= #{baseY}||'04')) OR ( #{baseY}||'04' > EDD AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(EDD,'YYYYMM'),-11),'YYYYMM') AND YYYYMM <= EDD) AND (AC221_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= EDD)) THEN 1 ELSE 0 END),0)) AS ACOL4 , (NVL(SUM(CASE WHEN ((YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'06','YYYYMM'),-12),'YYYYMM') AND YYYYMM <= #{baseY}||'05') AND (AC221_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= #{baseY}||'05')) OR ( #{baseY}||'05' > EDD AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(EDD,'YYYYMM'),-11),'YYYYMM') AND YYYYMM <= EDD) AND (AC221_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= EDD)) THEN 1 ELSE 0 END),0)) AS ACOL5 , (NVL(SUM(CASE WHEN ((YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'07','YYYYMM'),-12),'YYYYMM') AND YYYYMM <= #{baseY}||'06') AND (AC221_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= #{baseY}||'06')) OR ( #{baseY}||'06' > EDD AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(EDD,'YYYYMM'),-11),'YYYYMM') AND YYYYMM <= EDD) AND (AC221_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= EDD)) THEN 1 ELSE 0 END),0)) AS ACOL6 , (NVL(SUM(CASE WHEN ((YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'08','YYYYMM'),-12),'YYYYMM') AND YYYYMM <= #{baseY}||'07') AND (AC221_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= #{baseY}||'07')) OR ( #{baseY}||'07' > EDD AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(EDD,'YYYYMM'),-11),'YYYYMM') AND YYYYMM <= EDD) AND (AC221_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= EDD)) THEN 1 ELSE 0 END),0)) AS ACOL7 , (NVL(SUM(CASE WHEN ((YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'09','YYYYMM'),-12),'YYYYMM') AND YYYYMM <= #{baseY}||'08') AND (AC221_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= #{baseY}||'08')) OR ( #{baseY}||'08' > EDD AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(EDD,'YYYYMM'),-11),'YYYYMM') AND YYYYMM <= EDD) AND (AC221_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= EDD)) THEN 1 ELSE 0 END),0)) AS ACOL8 , (NVL(SUM(CASE WHEN ((YYYYMM >= TO_CHAR(ADD_MON