FROM ( SELECT /*+ use_hash(AC211 AC221 AC201 T2 T3) */ SUBSTR(AC211.SV_BIZ_DCLSF_CD,0,2) AS WRK_TYP , SUBSTR(AC221.WK_EXCN_DT,1,6) AS YYYYMM , AC221.WK_EXCN_DT AS AC221_WRK_DT , ST108.LNC_STRTDT AS ST108_LAUN_ST , ST108.LNC_EXDT AS ST108_LAUN_ED , (SELECT MAX(SUBSTR(SB1.LNC_EXDT,1,6)) FROM TB_SVST_NWPRD_M3_LNC_IZ SB1 WHERE SB1.LNC_EXDT LIKE #{baseY}||'%' AND SB1.PD_CD = AC211.PD_CD
AND SB1.PD_CD = #{pdCd}
AND SB1.PD_GRP_CD = #{pdGrp}
AND SB1.PD_GRP_CD = #{pdGrp}
AND SB1.PD_GRP_CD = #{pdGrp}
AND SB1.PD_GRP_CD = #{pdGrp}
AND SB1.PD_GRP_CD = #{pdGrp}
AND SB1.PD_GRP_CD IN ('5','7','8','9')
) AS EDD FROM TB_SVPD_CST_SVAS_IST_OJ_IZ AC211 INNER JOIN TB_SVPD_CST_SVAS_IST_ASN_IZ AC221 ON AC211.CST_SV_ASN_NO = AC221.CST_SV_ASN_NO INNER JOIN TB_SVPD_CST_SV_EXCN_IZ AC201 ON AC211.CNTR_NO = AC201.CNTR_NO AND AC211.CNTR_SN = AC201.CNTR_SN INNER JOIN TB_SVST_NWPRD_M3_LNC_IZ ST108 ON ST108.PD_CD = AC211.PD_CD AND ST108.USE_YN = 'Y' AND ST108.DTA_DL_YN = 'N' INNER JOIN WSMDBS.TB_PDBS_PD_BAS T2 /*상품기본*/ ON AC211.PD_CD = T2.PD_CD INNER JOIN WSMDBS.TB_PDBS_PD_ECOM_PRP_DTL T3 /*상품각사속성상세*/ ON AC211.PD_CD = T3.PD_CD AND T3.PD_EXTS_PRP_GRP_CD = 'PART' WHERE AC211.SV_BIZ_DCLSF_CD LIKE '11'||'%' AND (AC221.WK_EXCN_DT BETWEEN ST108.LNC_STRTDT AND ST108.LNC_EXDT) AND AC221.WK_CAN_RSON_CD IS NULL AND AC221.WK_PRGS_STAT_CD = '20'
AND TRIM(T3.PD_PRP_VAL20) = #{pdGrp}
AND TRIM(T3.PD_PRP_VAL20) = #{pdGrp}
AND TRIM(T3.PD_PRP_VAL20) = #{pdGrp}
AND TRIM(T3.PD_PRP_VAL20) = #{pdGrp}
AND TRIM(T3.PD_PRP_VAL20) = #{pdGrp}
AND TRIM(T3.PD_PRP_VAL20) IN ('5','7','8','9')
) UNION ALL SELECT '3' AS NM , 0 AS ACOL1 , 0 AS ACOL2 , 0 AS ACOL3 , 0 AS ACOL4 , 0 AS ACOL5 , 0 AS ACOL6 , 0 AS ACOL7 , 0 AS ACOL8 , 0 AS ACOL9 , 0 AS ACOL10 , 0 AS ACOL11 , 0 AS ACOL12 FROM DUAL
UNION ALL
