AND SUB1.PD_GRP_CD = #{pdGrp}
AND SUB1.PD_GRP_CD = #{pdGrp}
AND SUB1.PD_GRP_CD = #{pdGrp}
AND SUB1.PD_GRP_CD = #{pdGrp}
AND SUB1.PD_GRP_CD = #{pdGrp}
AND SUB1.PD_GRP_CD IN ('5','7','8','9')
) AS EDD , VS161.PD_GRP_CD FROM TB_SVPD_QLTY_MNGT_AS_DTL_IZ VS161 INNER JOIN WSMDBS.TB_SVPD_CST_SV_EXCN_IZ AC201 ON VS161.CNTR_NO = AC201.CNTR_NO AND VS161.CNTR_SN = AC201.CNTR_SN INNER JOIN TB_SVST_NWPRD_M3_LNC_IZ ST108 ON ST108.PD_CD = VS161.PD_CD AND ST108.USE_YN = 'Y' AND ST108.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_PDBS_PD_ECOM_PRP_DTL CYC_TP ON CYC_TP.PD_CD = AC201.SV_PD_CD AND CYC_TP.PD_EXTS_PRP_GRP_CD = 'SCHD' WHERE (VS161.WK_PRCSDT BETWEEN ST108.LNC_STRTDT AND ST108.LNC_EXDT) AND ((VS161.WK_PROCS_YM < '201901' AND (VS161.IMPTA_RSON_CD IN ('1','2','3','4','5','6') OR VS161.IMPTA_RSON_CD IS NULL)) OR (VS161.WK_PROCS_YM >= '201901' AND (VS161.BAD_DV_CD IN ('100R','500R','400R','700R') OR VS161.BAD_DV_CD IS NULL))) AND TRIM(VS161.PD_GRP_CD) != '11' AND ((VS161.SV_PROCS_FOM_CD != '114A') OR (VS161.SV_PROCS_FOM_CD IS NULL))
