 > EDD AND INSTDT >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'01','YYYYMM'),-4),'YYYYMM') AND INSTDT <= EDD) AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(EDD,'YYYYMM'),-11),'YYYYMM') AND YYYYMM <= EDD) AND (VS161_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= EDD) ) THEN PR ELSE 0 END),0)) AS ACOL9 , (NVL(SUM(CASE WHEN ((INSTDT >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'10','YYYYMM'),-4),'YYYYMM') AND INSTDT <= #{baseY}||'10') AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'10','YYYYMM'),-11),'YYYYMM') AND YYYYMM <= #{baseY}||'10') AND (VS161_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= #{baseY}||'10') OR (#{baseY}||'10' > EDD AND INSTDT >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'01','YYYYMM'),-4),'YYYYMM') AND INSTDT <= EDD) AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(EDD,'YYYYMM'),-11),'YYYYMM') AND YYYYMM <= EDD) AND (VS161_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= EDD) ) THEN PR ELSE 0 END),0)) AS ACOL10 , (NVL(SUM(CASE WHEN ((INSTDT >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'11','YYYYMM'),-4),'YYYYMM') AND INSTDT <= #{baseY}||'11') AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'11','YYYYMM'),-11),'YYYYMM') AND YYYYMM <= #{baseY}||'11') AND (VS161_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= #{baseY}||'11') OR (#{baseY}||'11' > EDD AND INSTDT >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'01','YYYYMM'),-4),'YYYYMM') AND INSTDT <= EDD) AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(EDD,'YYYYMM'),-11),'YYYYMM') AND YYYYMM <= EDD) AND (VS161_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= EDD) ) THEN PR ELSE 0 END),0)) AS ACOL11 , (NVL(SUM(CASE WHEN ((INSTDT >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'12','YYYYMM'),-4),'YYYYMM') AND INSTDT <= #{baseY}||'12') AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'12','YYYYMM'),-11),'YYYYMM') AND YYYYMM <= #{baseY}||'12') AND (VS161_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= #{baseY}||'12') OR (#{baseY}||'12' > EDD AND INSTDT >= TO_CHAR(ADD_MONTHS(TO_DATE(#{baseY}||'01','YYYYMM'),-4),'YYYYMM') AND INSTDT <= EDD) AND (YYYYMM >= TO_CHAR(ADD_MONTHS(TO_DATE(EDD,'YYYYMM'),-11),'YYYYMM') AND YYYYMM <= EDD) AND (VS161_WRK_DT BETWEEN ST108_LAUN_ST AND ST108_LAUN_ED) AND (SUBSTR(ST108_LAUN_ED,1,6) >= EDD) ) THEN PR ELSE 0 END),0)) AS ACOL12
FROM ( SELECT /*+ use_hash(VS161 AC201 CYC_TP) */ SUBSTR(VS161.SV_BIZ_DCLSF_CD,0,2) AS WRK_TYP , VS161.WK_PROCS_YM AS YYYYMM , WK_PRCSDT AS VS161_WRK_DT , SUBSTR(VS161.IST_DT, 1, 6) AS INSTDT , VS161.CST_SV_ASN_NO , ST108.LNC_STRTDT AS ST108_LAUN_ST , ST108.LNC_EXDT AS ST108_LAUN_ED , (SELECT MAX(SUBSTR(ST108.LNC_EXDT,1,6)) FROM TB_SVST_NWPRD_M3_LNC_IZ SUB1 WHERE SUB1.LNC_EXDT LIKE #{baseY}||'%' AND SUB1.PD_CD = SUB1.PD_CD
AND SUB1.PD_CD = #{pdCd}
AND SUB1.PD_GRP_CD = #{pdGrp}
AND SUB1.PD_GRP_CD = #{pdGrp}
AND SUB1.PD_GRP_CD = #{pdGrp}
AND SUB1.PD_GRP_CD = #{pdGrp}
AND SUB1.PD_GRP_CD = #{pdGrp}
AND SUB1.PD_GRP_CD IN ('5','7','8','9')
) AS EDD , NVL((SELECT SUM(CASE WHEN ST163.SV_BIZ_HCLSF_CD = '1' OR ST163.SV_BIZ_HCLSF_CD = '2' OR ST163.SV_BIZ_HCLSF_CD = '3' THEN T2.PDCT_UPRC ELSE 0 END) FROM TB_SVST_SV_WK_OSTR_IZ ST163 INNER JOIN TB_PDBS_PD_BAS T2 /*상품기본*/ ON ST163.ITM_PD_CD = T2.PD_CD WHERE ST163.CNTR_NO = VS161.CNTR_NO AND ST163.CNTR_SN = VS161.CNTR_SN AND ST163.CST_SV_ASN_NO = VS161.CST_SV_ASN_NO) , 0) AS PR FROM TB_SVPD_QLTY_MNGT_AS_DTL_IZ VS161 INNER JOIN WSMDBS.TB_SVPD_CST_SV_EXCN_IZ AC201 ON VS161.CNTR_NO = AC201.CNTR_NO AND VS161.CNTR_SN = AC201.CNTR_SN INNER JOIN TB_SVST_NWPRD_M3_LNC_IZ ST108 ON ST108.PD_CD = VS161.PD_CD AND ST108.USE_YN = 'Y' AND ST108.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_PDBS_PD_ECOM_PRP_DTL CYC_TP ON CYC_TP.PD_CD = AC201.SV_PD_CD AND CYC_TP.PD_EXTS_PRP_GRP_CD = 'SCHD' WHERE (VS161.WK_PRCSDT BETWEEN ST108.LNC_STRTDT AND ST108.LNC_EXDT) AND ((VS161.WK_PROCS_YM < '201901' AND (VS161.IMPTA_RSON_CD IN ('1','2','3','4','5','6') OR VS161.IMPTA_RSON_CD IS NULL)) OR (VS161.WK_PROCS_YM >= '201901' AND (VS161.BAD_DV_CD IN ('100R','500R','400R','700R') OR VS161.BAD_DV_CD IS NULL))) AND TRIM(VS161.PD_GRP_CD) != '11' AND ((VS161.SV_PROCS_FOM_CD != '114A') OR (VS161.SV_PROCS_FOM_CD IS NULL))
AND VS161.SV_BIZ_DCLSF_CD = #{svType}
AND VS161.BAD_DV_CD = #{badDivide}
AND VS161.PD_CD = #{pdCd}
AND VS161.PD_GRP_CD = #{pdGrp}
AND VS161.PD_GRP_CD = #{pdGrp}
AND VS161.PD_GRP_CD = #{pdGrp}
AND VS161.PD_GRP_CD = #{pdGrp}
AND VS161.PD_GRP_CD = #{pdGrp}
AND VS161.PD_GRP_CD IN ('5','7','8','9')
) )

mapper.insert:
INSERT
  INTO TB_SVST_NWPRD_M3_LNC_IZ
     (
       OTSC_DV_CD   /* 아웃소싱구분코드 */
     , PD_CD        /* 상품코드 */
     , ITM_KND_CD   /* 품목종류코드 */
     , PD_GRP_CD    /* 상품그룹코드 */
     , LNC_STRTDT   /* 출시시작일자 */
     , LNC_EXDT     /* 출시만기일자 */
     , USE_YN       /* 사용여부  */
     , DTA_DL_YN    /* 데이터 삭제여부 */

     )
VALUES
     (
       #{otscDvCd}
     , #{pdCd}
     , '4'
     , #{pdGrpCd}
     , #{lncStrtdt}
     , #{lncExdt}
     , 'Y'       /* 사용여부  */
     , 'N'    /* 데이터 삭제여부 */

     )

mapper.select:
SELECT COUNT(1)
  FROM TB_SVST_NWPRD_M3_LNC_IZ
 WHERE 1=1
   AND OTSC_DV_CD=#{otscDvCd}
   AND PD_CD=#{pdCd}
