mapper.select:
SELECT    CS_BIL_ATC_CD
        , BIL_OJ_AMT
        , BIL_CTR_SUM_AMT
        , (BIL_OJ_AMT - BIL_CTR_SUM_AMT) AS BIL_RVE_AMT
  FROM  WSMDBS.TB_SVPD_SV_CS_BIL_DTL
 WHERE    1=1
      AND CNTR_NO = #{cntrNo}
      AND CNTR_SN = #{cntrSn}
      AND CST_SV_ASN_NO = #{cstSvAsnNo}
      AND DTA_DL_YN = 'N'

mapper.insert:
INSERT INTO TB_SSOP_CTT_BAS /* 컨택기본 */ ( CTT_OJ_ID /* 컨택대상ID */ , CST_NO /* 고객번호 */ , CNTR_NO /* 계약번호 */ , CTT_RCP_DTM /* 컨택접수일시 */ , CTT_FSH_DTM /* 컨택완료일시 */ , CTT_PSIC_ID /* 컨택담당자ID */ , CTT_DUEDT /* 컨택예정일자 */ , CTT_EXP_DV_CD /* 컨택예정구분코드 */ , CTT_MO_CN /* 컨택메모내용 */ , DTA_DL_YN /* 데이터삭제여부 */
) SELECT #{cttOjId} , T2.CNTR_CST_NO AS CST_NO /* 고객번호 */ , T1.CNTR_NO AS CNTR_NO /* 계약번호 */ , TO_CHAR(SYSDATE, 'YYYYMMDD') AS CTT_RCP_DTM/* 컨택접수일시 */ , #{cttRcpDtm} AS CTT_FSH_DTM /* 컨택완료일시 */ , CASE WHEN (T1.CTT_PSIC_ID = '0' OR T1.CTT_PSIC_ID = NULL) THEN #{session.userId} ELSE T1.CTT_PSIC_ID END AS CTT_PSIC_ID /* 컨택담당자ID */ , #{cttDuedt} AS CTT_DUEDT /* 컨택예정일자..화면에서 받아옴 */ , '10' AS CTT_EXP_DV_CD /* 컨택예정구분코드 */ , #{cttMoCn} AS CTT_MO_CN /* 컨택메모내용 */ , 'N' AS DTA_DL_YN /* 데이터삭제여부 */
FROM TB_SSCT_CNTR_DTL T1 INNER JOIN TB_SSCT_CNTR_BAS T2 ON T2.CNTR_NO = T1.CNTR_NO AND T2.DTA_DL_YN = 'N' WHERE 1=1 AND T1.CNTR_NO = #{cntrNo} AND T1.CNTR_SN = #{cntrSn}

mapper.update:
UPDATE TB_SSOP_CTT_BAS
   SET CTT_RCP_DTM=#{cttRcpDtm}
     , CTT_DUEDT=#{cttDuedt}
     , CTT_MO_CN=#{cttMoCn}

 WHERE 1=1
   AND CTT_OJ_ID=#{cttOjId}

mapper.insert:
INSERT
  INTO TB_SSOP_CTT_DTL /* 컨택상세 */
     (
       CTT_OJ_ID
     , CTT_SN
     , CNTR_NO
     , CNTR_SN
     , CTT_AK_CN

     , CTT_DTL_MO_CN

     , DTA_DL_YN

     )
VALUES
     (
       #{cttOjId}
     , (SELECT NVL(MAX(TO_NUMBER(CTT_SN)) + 1, 1) FROM TB_SSOP_CTT_DTL WHERE CTT_OJ_ID=#{cttOjId})
     , #{cntrNo}
     , #{cntrSn}
     , #{cttMoCn}

     , ('컨택일: ' || #{beforeRcpDtm} || ' -> ' || #{cttRcpDtm}
       || ', 방문예정일:' || #{beforeDuedt} || ' -> ' || #{cttDuedt}
       || ', 전달사항:' || #{beforeMoCn} || ' -> ' || #{cttMoCn})

     , 'N'

     )

mapper.select:
SELECT WK_CAN_RSON_CD
     , WK_CAN_MO_CN
     , WK_PRGS_STAT_CD
  FROM TB_SVPD_CST_SVAS_IST_ASN_IZ
 WHERE 1=1
   AND CST_SV_ASN_NO=#{cstSvAsnNo}
   AND CNTR_NO=#{cntrNo}
   AND CNTR_SN=#{cntrSn}

mapper.insert:
INSERT
  INTO TB_SSOP_CTT_CH_HIST /* 컨택변경이력 */
     (
       CTT_OJ_ID            /* 컨택대상ID */
     , HIST_STRT_DTM        /* 이력시작일시 */
     , CST_NO               /* 고객번호 */
     , CNTR_NO              /* 고객번호 */
     , CTT_PRGS_STAT_CD     /* 컨택진행상태코드 */
     , CTT_RCP_DTM          /* 컨택접수일시 */
     , CTT_DUEDT            /* 컨택예정일자 */
     , DTA_DL_YN            /* 데이터삭제여부 */

     )
VALUES
     (
       #{cttOjId}
     , TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
     , (
         SELECT CNTR_CST_NO
           FROM TB_SSCT_CNTR_DTL T1
          INNER JOIN TB_SSCT_CNTR_BAS T2
             ON T2.CNTR_NO = T1.CNTR_NO
            AND T2.DTA_DL_YN = 'N'
          WHERE 1=1
            AND T1.CNTR_NO=#{cntrNo}
            AND T1.CNTR_SN=#{cntrSn}
       )
     , #{cntrNo}
     , #{cttPrgsStatCd}
     , #{cttRcpDtm}
     , #{cttDuedt}
     , 'N'

     )

INSERT
  INTO TB_SSOP_CTT_DCH_HIST /* 컨택상세변경이력 */
     (
       CTT_OJ_ID
     , HIST_STRT_DTM
     , CTT_SN
     , CNTR_NO
     , CNTR_SN
     , CTT_AK_CN

     , CTT_DTL_MO_CN

     , DTA_DL_YN

     )
VALUES
     (
       #{cttOjId}
     , TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
     , (SELECT NVL(MAX(TO_NUMBER(CTT_SN)) + 1, 1) FROM TB_SSOP_CTT_DTL WHERE CTT_OJ_ID=#{cttOjId})
     , #{cntrNo}
     , #{cntrSn}
     , #{cttMoCn}

     , ('컨택일: ' || #{beforeRcpDtm} || ' -> ' || #{cttRcpDtm}
       || ', 방문예정일:' || #{beforeDuedt} || ' -> ' || #{cttDuedt}
       || ', 전달사항:' || #{beforeMoCn} || ' -> ' || #{cttMoCn})

     , 'N'

     )

UPDATE TB_SVPD_CST_SVAS_IST_ASN_IZ
   SET WK_CAN_RSON_CD=#{canRson}
     , WK_CAN_MO_CN=#{canRsonDtlIz}

 WHERE 1=1
   AND CST_SV_ASN_NO=#{cstSvAsnNo}
   AND CNTR_NO=#{cntrNo}
   AND CNTR_SN=#{cntrSn}

 MERGE
  INTO TB_SVPD_CST_SVAS_IST_ASN_IZ A
 USING DUAL
    ON (A.CST_SV_ASN_NO=#{cstSvAsnNo}
   AND A.CNTR_NO=#{cntrNo}
   AND A.CNTR_SN=#{cntrSn})
  WHEN MATCHED THEN
UPDATE
   SET A.WK_CAN_RSON_CD=#{wkCanRsonCd}
     , A.WK_CAN_MO_CN=#{wkCanMoCn}

  WHEN NOT MATCHED THEN
INSERT
     (
       A.CST_SV_ASN_NO
     , A.CNTR_NO
     , A.CNTR_SN
     , A.CNTR_CST_NO
     , A.WK_CAN_RSON_CD
     , A.WK_CAN_MO_CN

     )
VALUES
     (
       #{cstSvAsnNo}
     , #{cntrNo}
     , #{cntrSn}
     , (
         SELECT TO_CHAR(CNTR_CST_NO)
           FROM (
                  SELECT T2.CNTR_CST_NO
                    FROM TB_SSCT_CNTR_DTL T1
                   INNER JOIN TB_SSCT_CNTR_BAS T2
                      ON T2.CNTR_NO = T1.CNTR_NO
                   WHERE 1=1
                     AND T1.CNTR_NO=#{cntrNo}
                     AND T1.CNTR_SN=#{cntrSn}
                     AND T2.DTA_DL_YN = 'N'
             )
     )
     , #{wkCanRsonCd}
     , #{wkCanMoCn}

     )

mapper.select:
SELECT CST_SV_ASN_NO
     , WK_PRGS_STAT_CD
  FROM TB_SVPD_CST_SVAS_IST_ASN_IZ
 WHERE 1=1
   AND CST_SV_ASN_NO=#{cstSvAsnNo}
   AND CNTR_NO=#{cntrNo}
   AND CNTR_SN=#{cntrSn}

mapper.insert:
INSERT
  INTO TB_SVPD_CST_SVAS_IST_ASN_HIST
     (
       AS_IST_ASN_ID
     , CST_SV_ASN_NO
     , CNTR_NO
     , CNTR_SN
     , CNTR_CST_NO
     , WK_CAN_RSON_CD
     , WK_CAN_MO_CN
     , DTA_DL_YN

     )
VALUES
     (
       TO_CHAR(SYSDATE, 'YYYYMMDD') || LPAD(SQ_SVPD_CST_SVAS_IST_ASN_HIST$AS_IST_ASN_ID.NEXTVAL, 12, 0)
     , #{cstSvAsnNo}
     , #{cntrNo}
     , #{cntrSn}
     , (
         SELECT CNTR_CST_NO
           FROM TB_SSCT_CNTR_BAS
          WHERE CNTR_NO=#{cntrNo}
     )
    , #{wkCanRsonCd}
    , #{wkCanMoCn}
     , 'N'

     )
