UNION ALL
SELECT CNTR_NO AS OJ_DTL_CNTR_NO , CNTR_SN AS OJ_DTL_CNTR_SN , CNTR_NO , CNTR_SN FROM TB_SVPD_CST_SV_BFSVC_ASN_IZ WHERE 1=1 AND ASN_OJ_YM = #{baseDt} ) ) , B1 AS ( SELECT DISTINCT MAIN.* FROM( SELECT A.* , CNTR_LST.CNTR_NO AS BASE_CNTR_NO , CNTR_LST.CNTR_SN AS BASE_CNTR_SN FROM TB_SVPD_BC_RPBL_AK_CST_IZ A , CNTR_LST WHERE( (A.CNTR_NO = OJ_DTL_CNTR_NO AND A.CNTR_SN = OJ_DTL_CNTR_SN) OR (A.CNTR_NO = CNTR_LST.CNTR_NO AND A.CNTR_SN = CNTR_LST.CNTR_SN)) AND (FST_PBL_DT LIKE #{baseDt}||'%' OR FNL_PBL_DT LIKE #{baseDt}||'%') ) MAIN ) , RGRP_INF AS ( SELECT --* T2.OG_TP_CD , T2.PRTNR_NO , T2.PRTNR_KNM , T1.OG_ID FROM TB_OGBS_MM_OG_IZ T1 INNER JOIN TB_OGBS_PRTNR_BAS T2 ON T2.PRTNR_NO = OG_UPBRNG_PRTNR_NO WHERE 1=1 AND T1.OG_UPBRNG_PRTNR_NO IS NOT NULL AND T1.BASE_YM = #{baseDt} AND T2.OG_TP_CD = 'W06' ) SELECT MAIN.RGRP_ID , MAIN.RGRP_NM||'('||MAIN.RGRP_CD||')' AS RGRP_NM , RGRP_INF.PRTNR_KNM AS BM_KNM , SUM(NVL(BS_CNTR_TOTAL,0)) AS BS_CNTR_TOTAL /* BS 전체 */ , SUM(NVL(BS_CNTR_TOTAL,0)) AS BS_CNTR_OBJ /* BS 대상 */ , NVL(SUM(CUR_MM_RE),0) AS CUR_MM_RE /* 당월 바코드 재발행 */ , NVL(SUM(PROCS_TOTAL),0) AS PROCS_TOTAL /* 처리 전체 */ , NVL(SUM(CUR_MM_RE_PROCS),0) AS CUR_MM_RE_PROCS /* 당월 바코드 재발행 중 처리 */ , NVL(SUM(SCAN_TOTAL),0) AS SCAN_TOTAL /* 스캔 전체 */ , NVL(SUM(CUR_MM_RE_SCAN),0) AS CUR_MM_RE_SCAN , NVL(SUM(HAND_TOTAL),0) AS HAND_TOTAL /* 수기 전체 */ , NVL(SUM(CUR_MM_RE_HAND), 0) AS CUR_MM_RE_HAND , NVL(SUM(RE_REQ), 0) AS RE_REQ_TOTAL /* 신청 */ , NVL(SUM(CUR_MM_RE_REQ), 0) AS CUR_MM_RE_REQ , NVL(SUM(YET_PROCS), 0) AS YET_PROCS , NVL(SUM(CUR_MM_RE_YET_PROCS),0) AS CUR_MM_RE_YET_PROCS , NVL(SUM(STP_TOTAL),0) AS STP_TOTAL , NVL(SUM(CUR_MM_RE_STP), 0) AS CUR_MM_RE_STP , NVL(SUM(SCAN_TOTAL) + SUM(HAND_TOTAL), 0) STICK_TOTAL /* 부착 건수 */ , NVL(SUM(CUR_MM_RE_SCAN) + SUM(CUR_MM_RE_HAND), 0) AS CUR_MM_RE_STICK /* 당월 재발행 부착 */ , (CASE WHEN NVL(SUM(BS_CNTR_TOTAL),0) > 0 THEN ROUND( (NVL(SUM(PROCS_TOTAL),0) / NVL(SUM(BS_CNTR_TOTAL),0))*100, 2) ELSE 0 END) AS RAT_PROCS_TOTAL /* 전체 처리율 */ , (CASE WHEN NVL(SUM(CUR_MM_RE),0) > 0 THEN ROUND( NVL(SUM(CUR_MM_RE_PROCS),0) / NVL(SUM(CUR_MM_RE),0)*100, 2) ELSE 0 END) AS RAT_RE_PROCS /* 재발행 처리율 */ , (CASE WHEN NVL(SUM(BS_CNTR_TOTAL),0) > 0 THEN ROUND( ((NVL(SUM(SCAN_TOTAL),0) + NVL(SUM(HAND_TOTAL),0)) / NVL(SUM(BS_CNTR_TOTAL),0))*100, 2) ELSE 0 END) AS RAT_STICK_TOTAL /* 전체 부착율 */ , (CASE WHEN NVL(SUM(CUR_MM_RE),0) > 0 THEN ROUND( ( (NVL(SUM(CUR_MM_RE_SCAN),0) + NVL(SUM(CUR_MM_RE_HAND),0)) / NVL(SUM(CUR_MM_RE),0) )*100 , 2) ELSE 0 END) AS RAT_RE_STICK /* 재발행 부착율 */ , (CASE WHEN NVL(SUM(PROCS_TOTAL),0) > 0 THEN ROUND(( NVL( SUM(SCAN_TOTAL),0 ) / NVL(SUM(PROCS_TOTAL),0))*100 , 2) ELSE 0 END) AS RAT_SCAN_TOTAL /* 전체 스캔율 */ , (CASE WHEN NVL(SUM(CUR_MM_RE_PROCS),0) > 0 THEN ROUND( (NVL(SUM(CUR_MM_RE_SCAN),0) / NVL(SUM(CUR_MM_RE_PROCS),0) )*100 , 2) ELSE 0 END) AS RAT_RE_SCAN /* 재발행 스캔율 */ FROM (SELECT /*+ INDEX(S2 PK_SVPD_CST_SV_EXCN_IZ) INDEX(S1 IX_SVPD_CST_SV_BFSVC_ASN_HIST_03) */ --S1.* OG_INF.RGRP_ID , OG_INF.RGRP_CD , OG_INF.RGRP_NM , OG_INF.GNLR_ID , 1 AS BS_CNTR_TOTAL /* BS 전체 */ , (CASE WHEN B1.BASE_CNTR_NO IS NOT NULL THEN 1 ELSE 0 END) AS CUR_MM_RE /* 당월 바코드 재발행 */ , (CASE WHEN S3.CNTR_NO IS NOT NULL THEN 1 ELSE 0 END) AS PROCS_TOTAL /* 처리 전체 */ , (CASE WHEN B1.BASE_CNTR_NO IS NOT NULL AND S3.CNTR_NO IS NOT NULL THEN 1 ELSE 0 END) AS CUR_MM_RE_PROCS /* 당월 바코드 재발행 중 처리 */ , (CASE WHEN S3.BC_IN_MTHD_CD = 'S' THEN 1 ELSE 0 END) AS SCAN_TOTAL /* 스캔 전체 */ , (CASE WHEN B1.BASE_CNTR_NO IS NOT NULL AND S3.BC_IN_MTHD_CD = 'S' THEN 1 ELSE 0 END) AS CUR_MM_RE_SCAN /* 당월 바코드 재발행 중 스캔 */ , (CASE WHEN S3.BC_NO IS NOT NULL AND (S3.BC_IN_MTHD_CD != 'S' OR S3.BC_IN_MTHD_CD IS NULL) THEN 1 ELSE 0 END) AS HAND_TOTAL /* 수기 전체 */ , (CASE WHEN B1.BASE_CNTR_NO IS NOT NULL AND S3.BC_NO IS NOT NULL AND (S3.BC_IN_MTHD_CD != 'S' OR S3.BC_IN_MTHD_CD IS NULL) THEN 1 ELSE 0 END) AS CUR_MM_RE_HAND /* 당월 바코드 재발행 중 수기 */ , (CASE WHEN S1.VST_PRGS_STAT_CD = '20' AND S3.BC_NO IS NULL THEN 1 ELSE 0 END) AS RE_REQ /* 전체 중 재발행 신청 */ , (CASE WHEN B1.BASE_CNTR_NO IS NOT NULL AND S1.VST_PRGS_STAT_CD = '20' AND S3.BC_NO IS NULL THEN 1 ELSE 0 END) AS CUR_MM_RE_REQ /* 재발행 중 재발행 신청 */ , (CASE WHEN S3.CNTR_NO IS NULL THEN 1 ELSE 0 END) AS YET_PROCS /* 전체 중 미처리 */ , (CASE WHEN B1.BASE_CNTR_NO IS NOT NULL AND S3.CNTR_NO IS NULL THEN 1 ELSE 0 END) AS CUR_MM_RE_YET_PROCS /* 당월 바코드 재발행 중 미처리 */ , (CASE WHEN S1.VST_PRGS_STAT_CD = '73' THEN 1 ELSE 0 END) AS STP_TOTAL /* 방문 중지 */ , (CASE WHEN B1.BASE_CNTR_NO IS NOT NULL AND S1.VST_PRGS_STAT_CD = '73' THEN 1 ELSE 0 END) AS CUR_MM_RE_STP /* 당월 바코드 재발행 중 방문중지 */ FROM TB_SVPD_CST_SV_BFSVC_ASN_IZ S1 /* 고객서비스BS배정내역 ac261 */ INNER JOIN TB_SVPD_CST_SV_EXCN_IZ S2 ON S1.CNTR_NO = S2.CNTR_NO AND S1.CNTR_SN = S2.CNTR_SN AND S1.CNFM_PSIC_PRTNR_NO IS NOT NULL AND S1.ASN_OJ_YM = SUBSTR(#{baseDt},1,6) INNER JOIN TB_OGBS_MM_PRTNR_IZ P1 ON S1.CNFM_PSIC_PRTNR_NO = P1.PRTNR_NO AND S1.ASN_OJ_YM = P1.BASE_YM AND S1.CNFM_PSIC_PRTNR_OG_TP_CD = P1.OG_TP_CD AND P1.OG_ID NOT IN ('71322') AND P1.PRTNR_NO != '372' INNER JOIN WSMDBS.TB_SSCT_CNTR_DTL T1 /* 계약상세 */ ON S1.CNTR_NO = T1.CNTR_NO AND S1.CNTR_SN = T1.CNTR_SN AND T1.DTA_DL_YN = 'N' INNER JOIN TB_SSCT_CNTR_ADR_REL T3 /*계약주소관계*/ ON S1.CNTR_NO = T3.DTL_CNTR_NO AND S1.CNTR_SN = T3.DTL_CNTR_SN AND T3.ADRPC_TP_CD IN ('2','3') /*1 계약주소, 2 배달주소, 3 설치주소*/ AND S1.ASN_OJ_YM BETWEEN SUBSTR(T3.VL_STRT_DTM,1,6) AND SUBSTR(T3.VL_END_DTM,1,6) INNER JOIN OG_INF ON P1.PRTNR_NO = OG_INF.PRTNR_NO AND P1.OG_TP_CD = OG_INF.OG_TP_CD LEFT OUTER JOIN B1 /* 바코드재발행요청고객내역 */ ON S2.CNTR_NO = B1.BASE_CNTR_NO AND S2.CNTR_SN = B1.BASE_CNTR_SN AND B1.BC_NO = S2.BC_NO LEFT OUTER JOIN TB_SVPD_CST_SV_WK_RS_IZ S3 /* 고객서비스작업결과내역 VS106 */ ON S1.CST_SV_ASN_NO = S3.CST_SV_ASN_NO WHERE S1.VST_PRGS_STAT_CD != '73' ) MAIN LEFT OUTER JOIN RGRP_INF ON RGRP_INF.OG_ID = MAIN.GNLR_ID GROUP BY MAIN.RGRP_ID , MAIN.RGRP_NM , MAIN.RGRP_CD , RGRP_INF.PRTNR_KNM ORDER BY MAIN.RGRP_CD
