INSERT INTO TB_SVPD_SLF_MNGT_SPP_STP_AK_IZ
     ( CNTR_NO
     , CNTR_SN
     , AK_SN
     , APY_DT
     , SPP_STP_DV_CD
     , TN1_STP_YM
     , TN2_STP_YM
     , TN3_STP_YM
     , TN4_STP_YM
     , SPP_AK_SPF_DT
     , OG_TP_CD
     , PRTNR_NO
     , DTA_DL_YN

     )
VALUES
     ( #{cntrNo}
     , #{cntrSn}
     , (SELECT NVL(MAX(AK_SN), 0) +1
          FROM TB_SVPD_SLF_MNGT_SPP_STP_AK_IZ
         WHERE CNTR_NO = #{cntrNo}
           AND CNTR_SN = TO_NUMBER(#{cntrSn})
       )
     , #{apyDt}
     , #{sppStpDvCd}
     , #{tn1StpYm}
     , #{tn2StpYm}
     , #{tn3StpYm}
     , #{tn4StpYm}
     , #{sppAkSpfDt}
     , (SELECT T1.OG_TP_CD
          FROM TB_OGBS_MM_PRTNR_IZ T1
         WHERE 1 = 1
           AND T1.BASE_YM = TO_CHAR(SYSDATE, 'YYYYMM')
           AND T1.OG_TP_CD LIKE 'W%'
           AND T1.PRTNR_NO = (SELECT X.PRTNR_NO
                                FROM TB_OGBS_MM_PRTNR_IZ X
                               WHERE 1 = 1
                                 AND X.BASE_YM = TO_CHAR(SYSDATE, 'YYYYMM')
                                 AND X.PRTNR_NO = #{prtnrNo}
                             )
       )
     , #{prtnrNo}
     , 'N'

     )
