mapper.select:
WITH CST_MPNO AS ( SELECT * FROM (SELECT CST_CTPLC_OJ_REFK_VAL , LOCARA_TNO , EXNO_ENCR , IDV_TNO , RANK() OVER (PARTITION BY CST_CTPLC_OJ_DV_CD, CST_CTPLC_OJ_REFK_VAL, CTPLC_TP_CD ORDER BY HIST_STRT_DTM DESC) AS RNK FROM TB_CUBS_CST_CTPLC_BAS X WHERE 1=1 AND CST_CTPLC_OJ_DV_CD = '03' /* 고객관계자 */ AND CTPLC_TP_CD = '03' /* 휴대전화번호 */ AND TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') BETWEEN HIST_STRT_DTM AND HIST_END_DTM AND DTA_DL_YN = 'N') WHERE RNK = 1 ) , CST_PNO AS ( SELECT * FROM (SELECT CST_CTPLC_OJ_REFK_VAL , LOCARA_TNO , EXNO_ENCR , IDV_TNO , RANK() OVER (PARTITION BY CST_CTPLC_OJ_DV_CD, CST_CTPLC_OJ_REFK_VAL, CTPLC_TP_CD ORDER BY HIST_STRT_DTM DESC) AS RNK FROM TB_CUBS_CST_CTPLC_BAS X WHERE 1=1 AND CST_CTPLC_OJ_DV_CD = '03' /* 고객관계자 */ AND CTPLC_TP_CD = '04' /* 전화번호 */ AND TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') BETWEEN HIST_STRT_DTM AND HIST_END_DTM AND DTA_DL_YN = 'N') WHERE RNK = 1 ) , CST_EML AS ( SELECT * FROM (SELECT CST_CTPLC_OJ_REFK_VAL , EMADR , RANK() OVER (PARTITION BY CST_CTPLC_OJ_DV_CD, CST_CTPLC_OJ_REFK_VAL, CTPLC_TP_CD ORDER BY HIST_STRT_DTM DESC) AS RNK FROM TB_CUBS_CST_CTPLC_BAS X WHERE 1=1 AND CST_CTPLC_OJ_DV_CD = '03' /* 고객관계자 */ AND CTPLC_TP_CD = '07' /* 이메일 */ AND TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') BETWEEN HIST_STRT_DTM AND HIST_END_DTM AND DTA_DL_YN = 'N') WHERE RNK = 1 ) , PART_MASTER AS (
) SELECT T1.CST_SV_ASN_NO , T1.CNTR_NO , T1.CNTR_SN , T5.SELL_OG_TP_CD AS SELL_PRTNR_OG_TP_CD , T5.SELL_PRTNR_NO , T10.PRTNR_KNM AS SELL_PRTNR_NM , T10.OG_CD AS SELL_PRTNR_OG_CD , T5.CNTR_CST_NO AS CST_NO , T6.CST_KNM AS CST_NM , T11.LOCARA_TNO AS CST_MPNO_1 , T11.EXNO_ENCR AS CST_MPNO_2 , T11.IDV_TNO AS CST_MPNO_3 , T12.LOCARA_TNO AS CST_PNO_1 , T12.EXNO_ENCR AS CST_PNO_2 , T12.IDV_TNO AS CST_PNO_3 , T13.EMADR AS CST_EMADR , T6.BZRNO , T8.RCGVP_KNM AS RCGVP_NM , T8.ADR_ID , T9.NEW_ADR_ZIP , T9.RNADR , T9.RDADR , T1.WK_EXCN_DT , T2.CLSF_CD_SRN_PRNT_CN , T4.OG_TP_CD AS PSIC_PRTNR_OG_TP_CD , T4.PRTNR_NO AS PSIC_PRTNR_NO , T14.PRTNR_KNM AS PSIC_PRTNR_NM , T3.PDCT_PD_CD AS PD_CD , T15.SVPD_NM_KOR AS PD_NM , T15.SVPD_NM_ABBR1 AS PD_ABBR_NM FROM TB_SVPD_CST_SV_BFSVC_ASN_IZ T1 INNER JOIN TB_SVPD_CST_SV_BFSVC_OJ_IZ T2 ON T2.CST_SV_ASN_NO = T1.CST_SV_ASN_NO AND T1.VST_PRGS_STAT_CD = '20' AND T1.DTA_DL_YN = 'N' AND T2.DTA_DL_YN = 'N' INNER JOIN TB_SVPD_CST_SV_EXCN_IZ T3 ON T3.CNTR_NO = T1.CNTR_NO AND T3.CNTR_SN = T1.CNTR_SN AND T3.DTA_DL_YN = 'N' INNER JOIN TB_SVPD_CST_SV_WK_RS_IZ T4 ON T4.CST_SV_ASN_NO = T1.CST_SV_ASN_NO AND T4.DTA_DL_YN = 'N' INNER JOIN TB_SSCT_CNTR_BAS T5 ON T5.CNTR_NO = T1.CNTR_NO INNER JOIN TB_CUBS_CST_BAS T6 ON T6.CST_NO = T5.CNTR_CST_NO INNER JOIN TB_SSCT_CNTR_ADR_REL T7 ON T7.DTL_CNTR_NO = T1.CNTR_NO AND T7.DTL_CNTR_SN = T1.CNTR_SN AND T7.ADRPC_TP_CD IN ('2', '3') AND TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') BETWEEN T7.VL_STRT_DTM AND T7.VL_END_DTM AND T7.DTA_DL_YN = 'N' INNER JOIN TB_SSCT_CNTR_ADRPC_BAS T8 ON T8.CNTR_ADRPC_ID = T7.CNTR_ADRPC_ID AND T8.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_GBCO_ADR_BAS T9 ON T9.ADR_ID = T8.ADR_ID AND T9.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_OGBS_PRTNR_BAS T10 ON T10.OG_TP_CD = T5.SELL_OG_TP_CD AND T10.PRTNR_NO = T5.SELL_PRTNR_NO LEFT OUTER JOIN CST_MPNO T11 ON T11.CST_CTPLC_OJ_REFK_VAL = T6.CST_NO LEFT OUTER JOIN CST_PNO T12 ON T12.CST_CTPLC_OJ_REFK_VAL = T6.CST_NO LEFT OUTER JOIN CST_EML T13 ON T13.CST_CTPLC_OJ_REFK_VAL = T6.CST_NO LEFT OUTER JOIN TB_OGBS_PRTNR_BAS T14 ON T14.OG_TP_CD = T4.OG_TP_CD AND T14.PRTNR_NO = T4.PRTNR_NO LEFT OUTER JOIN PART_MASTER T15 ON T15.SVPD_PD_CD = T3.PDCT_PD_CD WHERE 1=1 AND T1.VST_CNFMDT BETWEEN #{fromDate} AND #{toDate}
AND T1.CNTR_NO = #{cntrNo}
AND T1.CNTR_SN = #{cntrSn}
AND T5.CNTR_CST_NO = #{cstNo}
AND T6.CST_KNM = #{cstNm}
AND T11.LOCARA_TNO = #{pno1}
AND T11.EXNO_ENCR = #{pno2}
AND T11.IDV_TNO = #{pno3}
AND T12.LOCARA_TNO = #{pno1}
AND T12.EXNO_ENCR = #{pno2}
AND T12.IDV_TNO = #{pno3}
AND T6.BZRNO = #{bzrno}
AND T5.SELL_PRTNR_NO = #{sellPrtnrNo}
And T10.OG_CD >= #{fromOgCd}
And T10.OG_CD <= #{toOgCd}
ORDER BY T1.VST_CNFMDT DESC , T5.CNTR_CST_NO ASC , T1.CNTR_NO DESC , T1.CNTR_SN DESC
