mapper.select:
SELECT COUNT(*)
  FROM TB_RVCL_ITG_BIL_OJ_BAS
 WHERE DTA_DL_YN = 'N'
   AND SUBOD_CNTR_NO = #{cntrNo}
   AND SUBOD_CNTR_SN = #{cntrSn}

SELECT RBFD.BIL_NO, RBFD.BIL_DTL_SN
  FROM TB_RVCL_BIL_FNT_AK_DTL RBFD
  JOIN TB_RVCL_BIL_FNT_AK_BAS RBFB
    ON RBFD.BIL_NO = RBFB.BIL_NO
 WHERE RBFD.DTA_DL_YN = 'N'
   AND RBFB.DTA_DL_YN = 'N'
   AND RBFD.CNTR_NO = #{cntrNo}
   AND RBFD.CNTR_SN = #{cntrSn}
   AND RBFB.RGL_FNT_D = #{dsnWdrwFntD}

SELECT COUNT(*)
  FROM TB_RVCL_AC_FNT_DSN_WDRW_BAS
 WHERE DTA_DL_YN = 'Y'
   AND CNTR_NO = #{cntrNo}
   AND CNTR_SN = #{cntrSn}

mapper.update:
UPDATE TB_RVCL_AC_FNT_DSN_WDRW_BAS
   SET DTA_DL_YN = 'N'
     , VL_STRTDT = TO_CHAR(SYSDATE, 'YYYYMMDD')
     , VL_ENDDT = '99991231'
     , DSN_WDRW_AMT = #{dsnWdrwAmt}
     , DP_AMT = #{dpAmt}
     , FNT_YN = #{fntYn}
     , DSN_WDRW_FNT_D = #{dsnWdrwFntD}
     , DSN_WDRW_FNT_PRD_CD = #{dsnWdrwFntPrdCd}

 WHERE DTA_DL_YN = 'Y'
   AND CNTR_NO = #{cntrNo}
   AND CNTR_SN = #{cntrSn}

UPDATE TB_RVCL_AC_FNT_DSN_WDRW_REL
   SET DTA_DL_YN = 'N'
 WHERE DTA_DL_YN = 'Y'
   AND BIL_NO = #{bilNo}
   AND BIL_DTL_SN = #{bilDtlSn}
   AND CNTR_NO = #{cntrNo}
   AND CNTR_SN = #{cntrSn}

mapper.select:
SELECT COUNT(*)
  FROM TB_RVCL_AC_FNT_DSN_WDRW_BAS
 WHERE DTA_DL_YN = 'N'
   AND CNTR_NO = #{cntrNo}
   AND CNTR_SN = #{cntrSn}

mapper.insert:
INSERT INTO TB_RVCL_AC_FNT_DSN_WDRW_BAS (
       CNTR_NO
     , CNTR_SN
     , VL_STRTDT
     , VL_ENDDT
     , DSN_WDRW_AMT
     , UC_AMT
     , DP_AMT
     , FNT_YN
     , DSN_WDRW_FNT_D
     , DSN_WDRW_FNT_PRD_CD
     , DTA_DL_YN

    ) 
    VALUES 
    (
       #{cntrNo}
     , #{cntrSn}
     , TO_CHAR(SYSDATE, 'YYYYMMDD')
     , '99991231'
     , #{dsnWdrwAmt}
     , #{ucAmt}
     , #{dpAmt}
     , #{fntYn}
     , #{dsnWdrwFntD}
     , #{dsnWdrwFntPrdCd}
     , 'N'

    )

INSERT INTO TB_RVCL_AC_FNT_DSN_WDRW_HIST ( CNTR_NO , CNTR_SN , HIST_CH_DTM , VL_STRTDT , VL_ENDDT , DSN_WDRW_AMT , DP_AMT , FNT_YN , DSN_WDRW_FNT_D , DSN_WDRW_FNT_PRD_CD , DTA_DL_YN
) SELECT CNTR_NO , CNTR_SN , TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') AS HIST_CH_DTM , VL_STRTDT , VL_ENDDT , DSN_WDRW_AMT , DP_AMT , FNT_YN , DSN_WDRW_FNT_D , DSN_WDRW_FNT_PRD_CD , DTA_DL_YN
FROM TB_RVCL_AC_FNT_DSN_WDRW_BAS WHERE DTA_DL_YN = #{dtaDlYn} AND CNTR_NO = #{cntrNo} AND CNTR_SN = #{cntrSn}

mapper.select:
SELECT COUNT(*)
  FROM TB_RVCL_BIL_FNT_AK_DTL RBFD
  JOIN TB_RVCL_BIL_FNT_AK_BAS RBFB
    ON RBFD.BIL_NO = RBFB.BIL_NO
   AND RBFD.DTA_DL_YN = 'N'
   AND RBFB.DTA_DL_YN = 'N'
 WHERE RBFD.CNTR_NO = #{cntrNo}
   AND RBFD.CNTR_SN = #{cntrSn}
   AND RBFB.RGL_FNT_D = #{dsnWdrwFntD}

mapper.insert:
INSERT INTO TB_RVCL_AC_FNT_DSN_WDRW_REL (
        BIL_NO
      , BIL_DTL_SN
      , CNTR_NO
      , CNTR_SN
      , VL_STRTDT
      , VL_ENDDT
      , DTA_DL_YN

       )
         VALUES
        (
        #{bilNo}
      , #{bilDtlSn}
      , #{cntrNo}
      , #{cntrSn}
      , TO_CHAR(SYSDATE, 'YYYYMMDD')
      , '99991231'
      , 'N'

         )

mapper.update:
UPDATE TB_RVCL_AC_FNT_DSN_WDRW_BAS
   SET VL_STRTDT = TO_CHAR(SYSDATE, 'YYYYMMDD')
     , VL_ENDDT = '99991231'
     , DSN_WDRW_AMT = #{dsnWdrwAmt}
     , UC_AMT = #{ucAmt}
     , DP_AMT = #{dpAmt}
     , FNT_YN = #{fntYn}
     , DSN_WDRW_FNT_D = #{dsnWdrwFntD}
     , DSN_WDRW_FNT_PRD_CD = #{dsnWdrwFntPrdCd}

 WHERE DTA_DL_YN = 'N'
   AND CNTR_NO = #{cntrNo}
   AND CNTR_SN = #{cntrSn}

UPDATE TB_RVCL_AC_FNT_DSN_WDRW_BAS
   SET DTA_DL_YN = 'Y'
     , VL_ENDDT = TO_CHAR(SYSDATE, 'YYYYMMDD')

 WHERE DTA_DL_YN = 'N'
   AND CNTR_NO = #{cntrNo}
   AND CNTR_SN = #{cntrSn}

UPDATE TB_RVCL_AC_FNT_DSN_WDRW_REL
   SET DTA_DL_YN = 'Y'
     , VL_ENDDT = TO_CHAR(SYSDATE, 'YYYYMMDD')

 WHERE DTA_DL_YN = 'N'
   AND CNTR_NO = #{cntrNo}
   AND CNTR_SN = #{cntrSn}
   AND BIL_NO = #{bilNo}
   AND BIL_DTL_SN = #{bilDtlSn}
