mapper.select:
SELECT T3.DLPNR_NM /* 거래처명 */ , T2.BZRNO /* 사업자등록번호 */ , T4.CNTR_NO , T4.CNTR_NO || T4.CNTR_SN AS CNTR /* 계약상세번호 */ , T4.CNTR_SN /* 일련번호 */ , T4.SELL_AMT /* 금액 */ FROM TB_SSCT_CNTR_BAS T1 INNER JOIN TB_CUBS_CST_BAS T2 ON T2.CST_NO = T1.CNTR_CST_NO AND T2.COPN_DV_CD = '2' INNER JOIN TB_GBCO_DLPNR_BAS T3 ON T3.BZRNO = T2.BZRNO INNER JOIN TB_SSCT_CNTR_DTL T4 ON T4.CNTR_NO = T1.CNTR_NO WHERE 1 = 1
AND T2.BZRNO = #{bzrno}
AND T4.CNTR_NO = #{cntrNo}
AND T4.CNTR_SN = #{cntrSn}

mapper.insert:
INSERT INTO TB_RVDW_BILL_DP_IZ ( ITG_DP_NO /* 통합입금번호 */ , FNIT_CD /* 금융기관코드 */ , BILL_BND_NO /* 어음채권번호 */ , BILL_BND_AMT /* 어음채권금액 */ , BILL_RCP_DT /* 어음접수일자 */ , BILL_EXPR_DT /* 어음만기일자 */ , BILL_LN_PSB_DT /* 어음대출가능일자 */ , BILL_BND_HNDOV_DT /* 어음채권양도일자 */ , BILL_LN_PSB_AMT /* 어음대출가능금액 */ , BILL_DLPNR_NM /* 어음거래처명 */ , SELL_BZS_BZRNO /* 판매업체사업자등록번호 */ , PBL_BZS_BZRNO /* 발행업체사업자등록번호 */ , BILL_RMK_CN /* 어음비고내용 */ , RVE_AK_NO /* 수납요청번호 */ , DTA_DL_YN /* 삭제여부 */
)VALUES ( #{itgDpNo} /* 통합입금번호 o */ , #{fnitCd} /* 금융기관코드 */ , #{billBndNo} /* 어음채권번호 */ , #{billBndAmt} /* 어음채권금액 */ , #{billRcpDt} /* 어음접수일자 */ , #{billExprDt} /* 어음만기일자 */ , #{billLnPsbDt} /* 어음대출가능일자 */ , #{billBndHndovDt} /* 어음채권양도일자 */ , #{billLnPsbAmt} /* 어음대출가능금액 */ , #{billDlpnrNm} /* 어음거래처명 */ , #{sellBzsBzrno} /* 판매업체사업자등록번호 */ , #{pblBzsBzrno} /* 발행업체사업자등록번호 */ , #{billRmkCn} /* 어음비고내용 */ , #{rveAkNo} /* 수납요청번호 x*/ , 'N' /* 삭제여부 */
)

INSERT INTO TB_RVDW_BILL_DP_CNTR_REL_IZ (
       ITG_DP_NO
     , CNTR_NO
     , CNTR_SN
     , BILL_DP_AMT

)
VALUES (
       #{itgDpNo} /* 통합입금번호 */
     , #{cntrNo}
     , #{cntrSn}
     , #{billDpAmt} /* 금액 */

)

mapper.update:
UPDATE TB_RVDW_BILL_DP_CNTR_REL_IZ
   SET BILL_DP_AMT = #{billDpAmt}

 WHERE 1 = 1
   AND ITG_DP_NO = #{itgDpNo} /* 통합입금번호 */
   AND CNTR_NO = #{cntrNo}
   AND CNTR_SN = #{cntrSn}

mapper.select:
SELECT T1.ITG_DP_NO /*통합입금번호*/ , T3.RVE_CD/*수납코드*/ , T2.BILL_BND_NO /*채권번호*/ , T2.BILL_RMK_CN /*어음구분*/ , T2.BILL_RCP_DT /*접수일자*/ , T2.BILL_EXPR_DT/*만기일자*/ , T1.CNTR_NO||T1.CNTR_SN AS CNTR /*계약번호*/ , T1.CNTR_NO /*계약번호*/ , T1.CNTR_SN /*계약일련번호*/ , T1.BILL_DP_AMT /*입금금액*/ FROM TB_RVDW_BILL_DP_CNTR_REL_IZ T1 LEFT JOIN TB_RVDW_BILL_DP_IZ T2 ON T2.ITG_DP_NO = T1.ITG_DP_NO AND T2.DTA_DL_YN = 'N'
LEFT JOIN TB_RVDW_ITG_DP_BAS T3 ON T2.ITG_DP_NO = T3.ITG_DP_NO AND T3.DTA_DL_YN = 'N' WHERE 1 = 1
AND T1.ITG_DP_NO = #{itgDpNo}
AND T2.BILL_BND_NO = #{billBndNo}
AND T1.CNTR_NO = #{cntrNo}
AND T1.CNTR_SN = #{cntrSn}
ORDER BY T1.CNTR_NO , T1.CNTR_SN

