mapper.select:
SELECT T1.WK_DT --작업일자 , T1.WK_SN --작업일련번호 , T1.GIRO_BIZ_DV_CD --지로업무구분코드 , T1.GIRO_BIZ_TP_CD --지로업무유형코드 , T1.CNTR_NO ||T1.CNTR_SN AS CNTR --계약번호 , T1.CNTR_NO --계약번호 , T1.CNTR_SN --계약일련번호 , T1.CST_FNM -- 고객성명 , T1.SL_DT --매출일자 , NVL(T2.RENTAL_TN,T3.RENTAL_TN) AS RENTAL_TN -- 1.현재차월 , T4.STPL_PTRM AS RECAP_DUTY_PTRM_N --약정개월 , T1.STRT_GIRO_TN --시작지로회차 , T1.END_GIRO_TN --종료지로회차 , T1.THM0_AMT --0차월금액 , T1.ISTM_MCN --할부개월수 , T1.ISTM_AMT --할부금액 , T1.ISTM_DSC_AMT --할부할인금액 , T1.PY_AMT --납입금액 , T1.STPL_NMN_AMT --약정차월금액 , T1.EXN_NMN_AMT --만료차월금액 , T1.LTPAY_YN --후납여부 , T1.GIRO_RGLR_DV_CD --정기구분 FROM TB_RVDW_GIRO_OCR_MNGT_BAS T1 LEFT OUTER JOIN TB_CBCL_WELLS_SL_MM_CL_IZ T2 ON T2.CNTR_NO = T1.CNTR_NO AND T2.CNTR_SN = T1.CNTR_SN AND T2.SL_CL_YM = TO_CHAR(SYSDATE,'YYYYMM') AND T2.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_CBCL_WELLS_SL_MM_CL_IZ T3 ON T3.CNTR_NO = T1.CNTR_NO AND T3.CNTR_SN = T1.CNTR_SN AND T3.SL_CL_YM = TO_CHAR(ADD_MONTHS(SYSDATE ,-1),'YYYYMM') AND T3.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_SSCT_CNTR_DTL T4 ON T4.CNTR_NO = T1.CNTR_NO AND T4.CNTR_SN = T1.CNTR_SN WHERE 1 = 1
AND T1.WK_DT = #{wkDt}
AND T1.GIRO_RGLR_DV_CD = #{giroRglrDvCd}
AND T1.DTA_DL_YN = 'N' ORDER BY T1.STRT_GIRO_TN , T1.END_GIRO_TN , T1.CNTR_NO , T1.CNTR_SN

mapper.insert:
INSERT INTO TB_RVDW_GIRO_OCR_MNGT_BAS(
       WK_DT --작업일자
     , WK_SN  --작업일련번호
     , GIRO_BIZ_DV_CD
     , GIRO_BIZ_TP_CD
     , CNTR_NO
     , CNTR_SN
     , CST_FNM --고객명
     , SL_DT --매출일자
     , SELL_TAM
     , SUBSC_AMT
     , TK_AMT
     , CSH_BLAM
     , GIRO_FEE_AMT
     , CNTR_TAM
     , STRT_GIRO_TN --시작
     , END_GIRO_TN --종료
     , THM0_AMT --0차월
     , ISTM_MCN
     , ISTM_AMT
     , ISTM_DSC_AMT
     , PY_AMT
     , STPL_NMN_AMT
     , EXN_NMN_AMT
     , LTPAY_YN
     , GIRO_RGLR_DV_CD

)VALUES(
       #{wkDt} --작업일자
     , (SELECT NVL(MAX(WK_SN),0) FROM TB_RVDW_GIRO_OCR_MNGT_BAS WHERE WK_DT = #{wkDt}) + 1  --작업일련번호
     , #{giroBizDvCd}
     , #{giroBizTpCd}
     , #{cntrNo}
     , #{cntrSn}
     , #{cstFnm} --고객명
     , #{slDt} --매출일자
     , #{sellTam     }
     , #{subscAmt    }
     , #{tkAmt       }
     , #{cshBlam     }
     , #{giroFeeAmt }
     , #{cntrTam     }
     , #{strtGiroTn} --시작
     , #{endGiroTn} --종료
     , #{thm0Amt} --0차월
     , #{istmMcn}
     , #{istmAmt}
     , #{istmDscAmt}
     , #{pyAmt}
     , #{stplNmnAmt}
     , #{exnNmnAmt}
     , #{ltpayYn}
     , #{giroRglrDvCd}

)

mapper.update:
UPDATE TB_RVDW_GIRO_OCR_MNGT_BAS
   SET
       STRT_GIRO_TN = #{strtGiroTn}--시작
     , END_GIRO_TN = #{endGiroTn}--종료
     , THM0_AMT = #{thm0Amt} --0차월
     , ISTM_MCN = #{istmMcn}
     , ISTM_AMT = #{istmAmt}
     , ISTM_DSC_AMT = #{istmDscAmt}
     , PY_AMT = #{pyAmt}
     , STPL_NMN_AMT = #{stplNmnAmt}
     , EXN_NMN_AMT = #{exnNmnAmt}
     , LTPAY_YN = #{ltpayYn}

 WHERE WK_DT = #{wkDt}
   AND WK_SN = #{wkSn}

UPDATE TB_RVDW_GIRO_OCR_MNGT_BAS
   SET
       DTA_DL_YN = 'Y'

 WHERE WK_DT = #{wkDt}
   AND WK_SN = #{wkSn}

mapper.insert:
INSERT INTO TB_RVDW_GIRO_OCR_PBL_BAS(
     GIRO_OCR_PBL_DTM --발행일시
   , GIRO_OCR_PBL_SEQN --발행순번
   , GIRO_OCR_PBL_OJ_STRTDT --시작일자
   , GIRO_OCR_PBL_OJ_ENDDT --종료일자
   , GIRO_OCR_PBL_TOT_QTY --총수량

)VALUES(
     #{giroOcrPblDtm}
   , #{giroOcrPblSeqn}
   , #{giroOcrPblOjStrtdt}
   , #{giroOcrPblOjStrtdt}
   , #{giroOcrPblTotQty}

)

mapper.update:
UPDATE
       TB_RVDW_GIRO_OCR_PBL_BAS
   SET DTA_DL_YN = 'Y'

 WHERE GIRO_OCR_PBL_DTM = #{giroOcrPblDtm}
   AND GIRO_OCR_PBL_SEQN = #{giroOcrPblSeqn}

UPDATE TB_RVDW_GIRO_OCR_PBL_BAS
   SET GIRO_OCR_PRNT_DT = #{giroOcrPrntDt}

 WHERE 1 = 1
   AND GIRO_OCR_PBL_DTM = #{giroOcrPblDtm}
   AND GIRO_OCR_PBL_SEQN = #{giroOcrPblSeqn}
   AND DTA_DL_YN = 'N'

mapper.insert:
INSERT INTO TB_RVDW_GIRO_OCR_PRNT_IZ ( GIRO_OCR_PRNT_DT /*지로OCR출력일자*/ , PRNT_WK_SN /*출력작업일련번호*/ , PRNT_SN /*출력일련번호*/ , CNTR_NO /*계약번호*/ , CNTR_SN /*계약일련번호*/ , CST_FNM /*고객성명*/ , GIRO_BIZ_DV_CD /*지로업무구분코드*/ , GIRO_BIZ_TP_CD /*지로업무유형코드*/ , GIRO_AMT /*지로금액*/ , MPY_TN_YM /*납부회차년월*/ , PY_TMLM_DT /*납입기한일자*/ , PY_TN_NM /*납입회차명*/ , GIRO_INQNO /*지로조회번호*/ , ELC_MPY_NO /*전자납부번호*/ , GIRO_PLRCV_ADR_ID /*지로수신처주소ID*/
) SELECT #{giroOcrPrntDt} AS GIRO_OCR_PRNT_DT /*지로OCR출력일자*/ , GIRO_OCR_PBL_SN AS PRNT_WK_SN /*출력작업일련번호*/ , (SELECT NVL(MAX(PRNT_SN ), 0)+1 FROM TB_RVDW_GIRO_OCR_PRNT_IZ WHERE 1 = 1 AND GIRO_OCR_PRNT_DT = #{giroOcrPrntDt} ) AS PRNT_SN /*출력일련번호*/ , T2.CNTR_NO /*계약번호*/ , T2.CNTR_SN /*계약일련번호*/ , T2.CST_FNM /*고객성명*/ , T2.GIRO_BIZ_DV_CD /*지로업무구분코드*/ , T2.GIRO_BIZ_TP_CD /*지로업무유형코드*/ , T2.PY_AMT AS GIRO_AMT /*지로금액*/ , T2.GIRO_PY_YM AS MPY_TN_YM /*납부회차년월*/ , SL_DT AS PY_TMLM_DT /*납입기한일자*/ , T2.GIRO_TN_CN AS PY_TN_NM /*납입회차명*/ , GIRO_NO AS GIRO_INQNO /*지로조회번호*/ , T2.ELC_MPY_NO /*전자납부번호*/ , T2.GIRO_PLRCV_ADR_ID /*지로수신처주소ID*/
FROM TB_RVDW_GIRO_OCR_PBL_BAS T1 INNER JOIN TB_RVDW_GIRO_OCR_PBL_DTL T2 ON T2.GIRO_OCR_PBL_DTM = T1.GIRO_OCR_PBL_DTM AND T2.GIRO_OCR_PBL_SEQN = T1.GIRO_OCR_PBL_SEQN AND T2.DTA_DL_YN = 'N' WHERE 1 = 1 AND T1.GIRO_OCR_PBL_DTM = #{giroOcrPblDtm} AND T1.GIRO_OCR_PBL_SEQN = #{giroOcrPblSeqn}
