mapper.insert:
INSERT INTO TB_RVDW_GIRO_PLRCV_BAS(
    CNTR_NO
  , CNTR_SN
  , GIRO_BIZ_DV_CD --지로업무구분코드
  , GIRO_BIZ_TP_CD --지로업무유형코드 판매유형
  , CST_FNM --고객명
  , GIRO_PLRCV_ADR_ID /* 주소 ID*/
  , GIRO_PLRCV_RGST_DT /* 등록일자*/

)VALUES(
    #{cntrNo}
  , #{cntrSn}
  , #{giroBizDvCd}
  , #{giroBizTpCd}
  , #{cstFnm}
  , #{giroPlrcvAdrId}
  , SUBSTR(#{giroPlrcvRgstDt}, 0 , 8)

)

mapper.update:
MERGE INTO TB_RVDW_GIRO_PLRCV_BAS S
    USING DUAL
       ON (    S.CNTR_NO = #{cntrNo}
           AND S.CNTR_SN = #{cntrSn})
    WHEN MATCHED THEN
        UPDATE SET GIRO_PLRCV_ADR_ID = #{giroPlrcvAdrId} /* 주소 ID*/
                 , GIRO_PLRCV_RGST_DT = SUBSTR(#{giroPlrcvRgstDt},0,8) /* 등록일자*/

    WHEN NOT MATCHED THEN
        INSERT (CNTR_NO
	          , CNTR_SN
	          , GIRO_BIZ_DV_CD --지로업무구분코드
	          , GIRO_BIZ_TP_CD --지로업무유형코드 판매유형
	          , CST_FNM --고객명
	          , GIRO_PLRCV_ADR_ID /* 주소 ID*/
	          , GIRO_PLRCV_RGST_DT /* 등록일자*/
	           )
        VALUES (
	            #{cntrNo}
	          , #{cntrSn}
	          , #{giroBizDvCd}
	          , #{giroBizTpCd}
	          , #{cstFnm}
	          , #{giroPlrcvAdrId}
	          , SUBSTR(#{giroPlrcvRgstDt} , 0 , 8)
	          )

mapper.insert:
INSERT INTO TB_RVDW_GIRO_PLRCV_BAS_HIST(
    CNTR_NO
  , CNTR_SN
  , GIRO_BIZ_DV_CD --지로업무구분코드
  , GIRO_BIZ_TP_CD --지로업무유형코드 판매유형
  , CST_FNM --고객명
  , GIRO_PLRCV_ADR_ID /* 주소 ID*/
  , GIRO_PLRCV_RGST_DT /* 등록일자*/
  , HIST_CH_DTM

)VALUES(
    #{cntrNo}
  , #{cntrSn}
  , #{giroBizDvCd}
  , #{giroBizTpCd}
  , #{cstFnm}
  , #{giroPlrcvAdrId}
  , SUBSTR(#{giroPlrcvRgstDt} , 0 , 8)
  , TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')

)

mapper.select:
SELECT
       COUNT(1)
  FROM TB_SSCT_CNTR_DTL
 WHERE CNTR_NO = #{cntrNo}
   AND CNTR_SN = #{cntrSn}
   AND DTA_DL_YN = 'N'
