mapper.select:
SELECT T1.LIF_SPPT_YM /*라이프지원년월*/ , T1.LIF_CNTR_NO /* 라이프계약번호*/ , T1.LIF_CNTR_SN /*라이프일련번호*/
/*구분코드가 웰스399 일때*/ , T1.LIF_SPPT_AMT AS AMT /*라이프지원금액*/ , SUM(T1.LIF_SPPT_AMT) OVER (ORDER BY 1) SUM_AMT
/*구분코드가 웰스399이 아닐때*/ , T1.WELS_DP_AMT AS AMT /*웰스입금금액*/ , SUM(T1.WELS_DP_AMT) OVER (ORDER BY 1) SUM_AMT
, T1.LIF_REP_AMT /*라이프환수금액*/ , T1.LIF_SPPT_AGG_AMT /*라이프지원누계금액*/ , T1.WELS_CNTR_NO /*웰스계약번호*/ , T1.WELS_CNTR_SN /*웰스계약일련번호*/ , T1.LIF_ALNC_DV_CD /*라이프제휴구분코드*/ , T1.FST_RGST_DTM /*입력일자*/ , (SELECT BASE_PD_CD FROM TB_SSCT_CNTR_DTL WHERE 1 = 1 AND CNTR_NO = T1.WELS_CNTR_NO AND CNTR_SN = T1.WELS_CNTR_SN AND DTA_DL_YN = 'N') AS PD_CD
, F_CMZ_USR_NM(T1.FST_RGST_USR_ID) AS FST_RGST_USR_NM /*입력담당자명*/ , (SELECT X.EPNO FROM T_CMP_USR_ACO_M X WHERE X.USR_ID = T1.FST_RGST_USR_ID AND X.DEL_YN = 'N' ) AS FST_RGST_USR_ID /*입력담당자ID*/ FROM TB_RVDW_LIF_RENTAL_SPPT_BAS T1 /*라이프렌탈지원기본*/ LEFT OUTER JOIN TB_RVDW_RVE_DTL T2 /*수납상세*/ ON T2.CNTR_NO = T1.WELS_CNTR_NO AND T2.CNTR_SN = T1.WELS_CNTR_SN AND T2.KW_GRP_CO_CD = T1.KW_GRP_CO_CD AND T2.DTA_DL_YN = 'N' AND T2.DP_DV_CD = '1' /*입금*/ AND T2.DP_MES_CD = '06'/*입금수단코드 일단은 포인트로 넣어보자*/ AND T2.DP_TP_CD = '0604'/*입금유형코드 일단 웰스제휴로 넣어보자*/ AND SUBSTR(T2.RVE_DT,0,6) = TO_CHAR(ADD_MONTHS(TO_DATE(T1.LIF_SPPT_YM,'YYYYMM'),1),'YYYYMM') WHERE 1 = 1 AND T1.KW_GRP_CO_CD = #{session.companyCode}
AND T1.LIF_SPPT_YM = #{lifSpptYm} /*라이프지원년월*/
AND T1.LIF_ALNC_DV_CD = #{lifAlncDvCd} /*라이프제휴구분코드*/
/*구분코드가 웰스399 일때*/ AND T1.LIF_SPPT_AMT > 0 /*라이프지원금액이 0보다 클때*/
/*구분코드가 웰스399이 아닐때*/ AND T1.WELS_DP_AMT > 0 /*웰스입금금액이 0보다 클때*/

SELECT COUNT(LIF_SPPT_YM) AS COUNT_LIF , MAX(DP_DT) AS DP_DTM
/*구분코드가 웰스399 일때*/ , NVL(SUM(T1.LIF_SPPT_AMT),0) AS AMT_SUM /*라이프지원금액*/ , NVL(SUM(T1.LIF_SPPT_AMT) - SUM(T2.RVE_AMT),0) AS DP_BLAM
/*구분코드가 웰스399이 아닐때*/ , NVL(SUM(T1.WELS_DP_AMT),0) AS AMT_SUM /*라이프지원금액*/ , NVL(SUM(T1.WELS_DP_AMT) - SUM(T2.RVE_AMT),0) AS DP_BLAM
FROM TB_RVDW_LIF_RENTAL_SPPT_BAS T1 /*라이프렌탈지원기본*/ LEFT OUTER JOIN TB_RVDW_RVE_DTL T2 /*수납상세*/ ON T2.CNTR_NO = T1.WELS_CNTR_NO AND T2.CNTR_SN = T1.WELS_CNTR_SN AND T2.KW_GRP_CO_CD = T1.KW_GRP_CO_CD AND T2.DTA_DL_YN = 'N' AND T2.DP_DV_CD = '1' /*입금*/ AND T2.DP_MES_CD = '06'/*입금수단코드 일단은 넣어보자*/ AND T2.DP_TP_CD = '0604'/*입금유형코드 일단 넣어보자*/ AND SUBSTR(T2.RVE_DT,0,6) = TO_CHAR(ADD_MONTHS(TO_DATE(T1.LIF_SPPT_YM,'YYYYMM'),1),'YYYYMM') WHERE 1 = 1 AND T1.KW_GRP_CO_CD = #{session.companyCode}
AND T1.LIF_SPPT_YM = #{lifSpptYm} /*라이프지원년월*/
AND T1.LIF_ALNC_DV_CD = #{lifAlncDvCd} /*라이프제휴구분코드*/
/*구분코드가 웰스399 일때*/ AND T1.LIF_SPPT_AMT > 0 /*라이프지원금액이 0보다 클때*/
/*구분코드가 웰스399이 아닐때*/ AND T1.WELS_DP_AMT > 0 /*웰스입금금액이 0보다 클때*/

mapper.insert:
MERGE INTO TB_RVDW_LIF_RENTAL_SPPT_BAS T1 USING DUAL ON ( T1.KW_GRP_CO_CD = #{session.companyCode} AND T1.LIF_SPPT_YM = #{lifSpptYm} AND T1.LIF_CNTR_NO = #{lifCntrNo} AND T1.LIF_CNTR_SN = #{lifCntrSn} ) WHEN MATCHED THEN UPDATE SET WELS_CNTR_NO = #{welsCntrNo}/*웰스계약번호*/ , WELS_CNTR_SN = #{welsCntrSn}/*웰스계약일련번호*/ , LIF_ALNC_PD_CD = #{lifAlncPdCd} /*상품*/ , LIF_ALNC_PD_NM = #{lifAlncPdNm}/*상품명*/
, LIF_SPPT_AMT = #{lifSpptAmt}/*지원금액*/
, WELS_DP_AMT = #{lifSpptAmt} /*웰스입금금액*/
, LIF_ALNC_DV_CD = #{lifAlncDvCd}/*제휴구분코드*/ , LIF_REP_AMT = #{lifRepAmt}/*라이프환수금액*/ , LIF_SPPT_AGG_AMT = #{lifSpptAggAmt}/*라이프지원누계금액*/
WHEN NOT MATCHED THEN INSERT ( WELS_CNTR_NO /*웰스계약번호*/ , WELS_CNTR_SN /*웰스계약일련번호*/ , LIF_ALNC_PD_CD /*상품*/ , LIF_ALNC_PD_NM /*상품명*/
, LIF_SPPT_AMT /*라이프지원금액*/
, WELS_DP_AMT /*웰스입금금액*/
, LIF_ALNC_DV_CD /*제휴구분코드*/ , LIF_REP_AMT /*라이프환수금액*/ , LIF_SPPT_AGG_AMT /*라이프지원누계금액*/ , KW_GRP_CO_CD , LIF_SPPT_YM , LIF_CNTR_NO , LIF_CNTR_SN
) VALUES ( #{welsCntrNo} /*웰스계약번호*/ , #{welsCntrSn} /*웰스계약일련번호*/ , #{lifAlncPdCd} /*상품*/ , #{lifAlncPdNm} /*상품명*/ , #{lifSpptAmt} /*지원금액*/ , #{lifAlncDvCd} /*제휴구분코드*/ , #{lifRepAmt} /*라이프환수금액*/ , #{lifSpptAggAmt} /*라이프지원누계금액*/ , #{session.companyCode} , #{lifSpptYm} , #{lifCntrNo} , #{lifCntrSn}
)

mapper.update:
UPDATE TB_RVDW_ITG_DP_BAS
   SET DP_CPRCNF_AMT = NVL(DP_CPRCNF_AMT,0) + #{dpCprcnfAmt}
     , DP_BLAM = NVL(DP_BLAM,0) - #{dpCprcnfAmt}
     , RVE_AK_NO = #{rveAkNo}


 WHERE 1 = 1
   AND KW_GRP_CO_CD = #{session.companyCode}
   AND ITG_DP_NO = #{itgDpNo}
