.RFND_RSON_CN AS RFND_RSON_CN /*환불사유내용*/ , T2.RFND_CSH_AK_AMT AS RFND_EXP_AMT /*환불예정금액*/ , #{rfndDsbDuedt} AS RFND_FSH_DT /*환불완료일자*/ , T2.RFND_CSH_AK_AMT AS RFND_DSB_AMT /*환불지급금액*/ , #{rfndDsbDuedt} AS RFND_DSB_DUEDT /*환불지급예정일자*/ , NULL AS RFND_PSP_DC /*환불지연일수*/ , NULL AS RFND_DSB_PSP_INT /*환불지급지연이자*/ , NULL AS RFND_SLIP_CRT_DT /*환불전표생성일자*/ , NULL AS RFND_SLPNO /*환불전표번호*/ , NULL AS MLG_TP_CD /*마일리지유형코드*/ , NULL AS MLG_RV_NO /*마일리지적립번호*/ , T3.ITG_DP_NO AS ITG_DP_NO /*통합입금번호*/ , T3.RVE_NO AS RVE_NO /*수납번호*/ , NULL AS PY_AMT /*납입금액*/ , NULL AS DLQ_AMT /*연체금액*/ , NULL AS BOR_AMT /*위약금액*/ , NULL AS RFND_EVID_MTR_FILE_ID /*환불증빙자료파일ID*/ , NULL AS DPRCP_MTR_FILE_ID /*입금증자료파일ID*/ , NULL AS POFAT_MTR_FILE_ID /*위임장자료파일ID*/ , NULL AS IDF_MTR_FILE_ID /*신분증자료파일ID*/ , NULL AS FML_REL_CRTFD_FILE_ID /*가족관계증명서파일ID*/
FROM TB_RVDW_RFND_AK_BAS T1 INNER JOIN TB_RVDW_RFND_AK_DTL T2 ON ( T2.KW_GRP_CO_CD = T1.KW_GRP_CO_CD AND T2.RFND_AK_NO = T1.RFND_AK_NO AND T2.DTA_DL_YN = 'N') INNER JOIN TB_RVDW_RVE_DTL T3 ON ( T3.KW_GRP_CO_CD = T2.KW_GRP_CO_CD AND T3.RVE_NO = T2.RVE_NO AND T3.RVE_SN = T2.RVE_SN AND T3.CNTR_NO = T2.CNTR_NO AND T3.CNTR_SN = T2.CNTR_SN AND T3.DTA_DL_YN = 'N') WHERE 1 = 1 AND T1.KW_GRP_CO_CD = #{session.companyCode} AND T1.RFND_AK_NO = #{rfndAkNo} AND T2.RFND_CSH_AK_AMT > 0 AND T2.CNTR_NO = #{cntrNo} AND T2.CNTR_SN = #{cntrSn} AND T2.RVE_NO = #{rveNo} AND T2.RVE_SN = #{rveSn} AND T1.DTA_DL_YN = 'N'

INSERT INTO TB_RVDW_RFND_RCP_DTL ( KW_GRP_CO_CD /*교원그룹회사코드*/ , RFND_RCP_NO /*환불접수번호*/ , RFND_RCP_DTL_SN /*환불접수상세일련번호*/ , RFND_TP_CD /*환불유형코드*/ , RFND_DV_CD /*환불구분코드*/ , RFND_DP_KND_CD /*환불입금종류코드*/ , RFND_DSB_DV_CD /*환불지급구분코드*/ , CSH_RFND_DV_CD /*현금환불구분코드*/ , RFND_AC_DV_CD /*환불계좌구분코드*/ , RFND_RNTF_EXST_YN /*환불손료존재여부*/ , CSH_RFND_FNIT_CD /*현금환불금융기관코드*/ , CSH_RFND_ACNO_ENCR /*현금환불계좌번호암호화*/ , CSH_RFND_ACOWN_NM /*현금환불계좌주명*/ , CSH_RFND_ADRS_DV_CD /*현금환불수취인구분코드*/ , CSH_RFND_DLGPS_NM /*현금환불대표자명*/ , CARD_RFND_FNIT_CD /*카드환불금융기관코드*/ , CARD_RFND_CRDCD_APR_STRT_DT /*카드환불신용카드승인시작일자*/ , CARD_RFND_CRDCD_APR_END_DT /*카드환불신용카드승인종료일자*/ , CARD_RFND_CRCDNO_ENCR /*카드환불신용카드번호암호화*/ , CARD_RFND_CRDCD_ISTM_MCN /*카드환불신용카드할부개월수*/ , CARD_RFND_CRCDONR_NM /*카드환불신용카드주명*/ , CARD_RFND_CRDCD_APRNO /*카드환불신용카드승인번호*/ , CARD_RFND_CRDCD_APR_AMT /*카드환불신용카드승인금액*/ , CARD_RFND_FEE /*카드환불수수료*/ , CARD_RFND_FER /*카드환불수수료율*/ , BLTF_RFND_DV_CD /*전금환불구분코드*/ , BLTF_RFND_TP_CD /*전금환불유형코드*/ , BLTF_RFND_MB_DV_CD /*전금환불회원구분코드*/ , BLTF_OJ_CNTR_NO /*전금대상계약번호*/ , BLTF_OJ_CNTR_SN /*전금대상계약일련번호*/ , BLTF_BF_VAC_NO_ENCR /*전금이전가상계좌번호암호화*/ , BLTF_AF_VAC_NO_ENCR /*전금이후가상계좌번호암호화*/ , KMBRSP_RFND_AK_AMT /*K멤버스포인트환불요청금액*/ , KMBRSM_RFND_AK_AMT /*K멤버스머니환불요청금액*/ , KMBRSC_RFND_AK_AMT /*K멤버스캐시환불요청금액*/ , SMTML_RFND_AK_AMT /*스마트마일리지환불요청금액*/ , ACTML_RFND_AK_AMT /*활동마일리지환불요청금액*/ , ETC_RFND_TP_CD /*기타환불유형코드*/ , ETC_RFND_RQR_NM /*기타환불요청자명*/ , RFND_AK_AMT /*환불요청금액*/ , RFND_DDTN_AMT /*환불공제금액*/ , RFND_RSON_CD /*환불사유코드*/ , RFND_RSON_CN /*환불사유내용*/ , RFND_EXP_AMT /*환불예정금액*/ , RFND_FSH_DT /*환불완료일자*/ , RFND_DSB_AMT /*환불지급금액*/ , RFND_DSB_DUEDT /*환불지급예정일자*/ , RFND_PSP_DC /*환불지연일수*/ , RFND_DSB_PSP_INT /*환불지급지연이자*/ , RFND_SLIP_CRT_DT /*환불전표생성일자*/ , RFND_SLPNO /*환불전표번호*/ , MLG_TP_CD /*마일리지유형코드*/ , MLG_RV_NO /*마일리지적립번호*/ , ITG_DP_NO /*통합입금번호*/ , RVE_NO /*수납번호*/ , PY_AMT /*납입금액*/ , DLQ_AMT /*연체금액*/ , BOR_AMT /*위약금액*/ , RFND_EVID_MTR_FILE_ID /*환불증빙자료파일ID*/ , DPRCP_MTR_FILE_ID /*입금증자료파일ID*/ , POFAT_MTR_FILE_ID /*위임장자료파일ID*/ , IDF_MTR_FILE_ID /*신분증자료파일ID*/ , FML_REL_CRTFD_FILE_ID /*가족관계증명서파일ID*/
) SELECT T1.KW_GRP_CO_CD /*교원그룹회사코드*/ , #{rfndRcpNo} AS RFND_RCP_NO /*환불접수번호*/ , (SELECT NVL(MAX(RFND_RCP_DTL_SN) , 0) + 1 FROM TB_RVDW_RFND_RCP_DTL WHERE KW_GRP_CO_CD = #{kwGrpCoCd} AND RFND_RCP_NO = #{rfndRcpNo}) /*환불접수상세일련번호*/ , '01' AS RFND_TP_CD /*환불유형코드*/ , '02' AS RFND_DV_CD /*환불구분코드*/ , '' AS RFND_DP_KND_CD /*환불입금종류코드*/ , '02' AS RFND_DSB_DV_CD /*환불지급구분코드*/ , '03' AS CSH_RFND_DV_CD /*현금환불구분코드*/ , '02' AS RFND_AC_DV_CD /*환불계좌구분코드*/ , NULL AS RFND_RNTF_EXST_YN /*환불손료존재여부*/ , NULL AS CSH_RFND_FNIT_CD /*현금환불금융기관코드*/ , NULL AS CSH_RFND_ACNO_ENCR /*현금환불계좌번호암호화*/ , NULL AS CSH_RFND_ACOWN_NM /*현금환불계좌주명*/ , '01' AS CSH_RFND_ADRS_DV_CD /*현금환불수취인구분코드*/ , NULL AS CSH_RFND_DLGPS_NM /*현금환불대표자명*/ , T4.CRDCD_FNIT_CD AS CARD_RFND_FNIT_CD /*카드환불금융기관코드*/ , T5.APR_DTM AS CARD_RFND_CRDCD_APR_STRT_DT /*카드환불신용카드승인시작일자*/ , T5.RCV_DTM AS CARD_RFND_CRDCD_APR_END_DT /*카드환불신용카드승인종료일자*/ , T4.CRCDNO_ENCR AS CARD_RFND_CRCDNO_ENCR /*카드환불신용카드번호암호화*/ , T4.CRDCD_ISTM_MCN AS CARD_RFND_CRDCD_ISTM_MCN /*카드환불신용카드할부개월수*/ , T4.CRCDONR_NM AS CARD_RFND_CRCDONR_NM /*카드환불신용카드주명*/ , T4.CRDCD_APR_DTM AS CARD_RFND_CRDCD_APRNO /*카드환불신용카드승인번호*/ , T2.RFND_CARD_AK_AMT AS CARD_RFND_CRDCD_APR_AMT /*카드환불신용카드승인금액*/ , T2.CRDCD_FEE_AMT AS CARD_RFND_FEE /*카드환불수수료*/ , T2.CRDCD_FER AS CARD_RFND_FER /*카드환불수수료율*/ , NULL AS BLTF_RFND_DV_CD /*전금환불구분코드*/ , NULL AS BLTF_RFND_TP_CD /*전금환불유형코드*/ , NULL AS BLTF_RFND_MB_DV_CD /*전금환불회원구분코드*/ , NULL AS BLTF_OJ_CNTR_NO /*전금대상계약번호*/ , NULL AS BLTF_OJ_CNTR_SN /*전금대상계약일련번호*/ , NULL AS BLTF_BF_VAC_NO_ENCR /*전금이전가상계좌번호암호화*/ , NULL AS BLTF_AF_VAC_NO_ENCR /*전금이후가상계좌번호암호화*/ , NULL AS KMBRSP_RFND_AK_AMT /*K멤버스포인트환불요청금액*/ , NULL AS KMBRSM_RFND_AK_AMT /*K멤버스머니환불요청금액*/ , NULL AS KMBRSC_RFND_AK_AMT /*K멤버스캐시환불요청금액*/ , NULL AS SMTML_RFND_AK_AMT /*스마트마일리지환불요청금액*/ , NULL AS ACTML_RFND_AK_AMT /*활동마일리지환불요청금액*/ , NULL AS ETC_RFND_TP_CD /*기타환불유형코드*/ , NULL AS ETC_RFND_RQR_NM /*기타환불요청자명*/ , T2.RFND_CARD_AK_AMT AS RFND_AK_AMT /*환불요청금액*/ , NULL AS RFND_DDTN_AMT /*환불공제금액*/ , T2.RFND_RSON_CD AS RFND_RSON_CD /*환불사유코드*/ , T2.RFND_RSON_CN AS RFND_RSON_CN /*환불사유내용*/ , T2.RFND_CARD_AK_AMT AS RFND_EXP_AMT /*환불예정금액*/ , #{rfndDsbDuedt} AS RFND_FSH_DT /*환불완료일자*/ , T2.RFND_CARD_AK_AMT AS RFND_DSB_AMT /*환불지급금액*/ , #{rfndDsbDuedt} AS RFND_DSB_DUEDT /*환불지급예정일자*/ , NULL AS RFND_PSP_DC /*환불지연일수*/ , NULL AS RFND_DSB_PSP_INT /*환불지급지연이자*/ , NULL AS RFND_SLIP_CRT_DT /*환불전표생성일자*/ , NULL AS RFND_SLPNO /*환불전표번호*/ , NULL AS MLG_TP_CD /*마일리지유형코드*/ , NULL AS MLG_RV_NO /*마일리지적립번호*/ , T3.ITG_DP_NO AS ITG_DP_NO /*통합입금번호*/ , T3.RVE_NO AS RVE_NO /*수납번호*/ , NULL AS PY_AMT /*납입금액*/ , NULL AS DLQ_AMT /*연체금액*/ , NULL AS BOR_AMT /*위약금액*/ , NULL AS RFND_EVID_MTR_FILE_ID /*환불증빙자료파일ID*/ , NULL AS DPRCP_MTR_FILE_ID /*입금증자료파일ID*/ , NULL AS POFAT_MTR_FILE_ID /*위임장자료파일ID*/ , NULL AS IDF_MTR_FILE_ID /*신분증자료파일ID*/ , NULL AS FML_REL_CRTFD_FILE_ID /*가족관계증명서파일ID*/
FROM TB_RVDW_RFND_AK_BAS T1 INNER JOIN TB_RVDW_RFND_AK_DTL T2 ON ( T2.KW_GRP_CO_CD = T1.KW_GRP_CO_CD AND T2.RFND_AK_NO = T1.RFND_AK_NO AND T2.DTA_DL_YN = 'N') INNER JOIN TB_RVDW_RVE_DTL T3 ON ( T3.KW_GRP_CO_CD = T2.KW_GRP_CO_CD AND T3.RVE_NO = T2.RVE_NO AND T3.RVE_SN = T2.RVE_SN AND T3.CNTR_NO = T2.CNTR_NO AND T3.CNTR_SN = T2.CNTR_SN AND T3.DTA_DL_YN = 'N') INNER JOIN TB_RVDW_ITG_DP_BAS T4 ON ( T4.ITG_DP_NO = T3.ITG_DP_NO AND T4.DTA_DL_YN = 'N') LEFT OUTER JOIN TB_RVDW_CRDCD_RTM_TRD_IZ T5 ON ( T5.CARD_APRNO = T4.CARD_KND_CD AND T5.CRCDNO_ENCR = T4.CRCDNO_ENCR AND T5.DTA_DL_YN = 'N') WHERE 1 = 1 AND T1.KW_GRP_CO_CD = #{session.companyCode} AND T1.RFND_AK_NO = #{rfndAkNo} AND T2.RFND_CSH_AK_AMT > 0 AND T2.CNTR_NO = #{cntrNo} AND T2.CNTR_SN = #{cntrSn} AND T2.RVE_NO = #{rveNo} AND T2.RVE_SN = #{rveSn} AND T1.DTA_DL_YN = 'N'

