FROM TB_RVDW_RFND_AK_BAS T1 INNER JOIN TB_RVDW_RFND_AK_DTL T2 ON ( T2.KW_GRP_CO_CD = T1.KW_GRP_CO_CD AND T2.RFND_AK_NO = T1.RFND_AK_NO AND T2.DTA_DL_YN = 'N') INNER JOIN TB_RVDW_RVE_DTL T3 ON ( T3.KW_GRP_CO_CD = T2.KW_GRP_CO_CD AND T3.RVE_NO = T2.RVE_NO AND T3.RVE_SN = T2.RVE_SN AND T3.CNTR_NO = T2.CNTR_NO AND T3.CNTR_SN = T2.CNTR_SN AND T3.DTA_DL_YN = 'N') INNER JOIN TB_RVDW_RFND_AK_BLTF_DTL T4 ON ( T4.KW_GRP_CO_CD = T2.KW_GRP_CO_CD AND T4.RFND_AK_NO = T2.RFND_AK_NO AND T4.CNTR_NO = T2.CNTR_NO AND T4.CNTR_SN = T2.CNTR_SN AND T4.RVE_NO = T2.RVE_NO AND T4.RVE_SN = T2.RVE_SN ) WHERE 1 = 1 AND T1.KW_GRP_CO_CD = #{session.companyCode} AND T1.RFND_AK_NO = #{rfndAkNo} AND T2.RFND_BLTF_AK_AMT > 0 AND T2.CNTR_NO = #{cntrNo} AND T2.CNTR_SN = #{cntrSn} AND T2.RVE_NO = #{rveNo} AND T2.RVE_SN = #{rveSn} AND T1.DTA_DL_YN = 'N'

mapper.update:
UPDATE TB_RVDW_RFND_AK_BAS
       SET RFND_AK_STAT_CD  = #{procsDv}
         , RFND_PROCS_DV_CD = #{procsDv}

 WHERE 1 = 1
   AND KW_GRP_CO_CD = #{session.companyCode}
   AND RFND_AK_NO   = #{rfndAkNo}
   AND DTA_DL_YN    = 'N'

mapper.insert:
INSERT INTO TB_RVDW_RFND_RCP_BAS_HIST ( KW_GRP_CO_CD /*교원그룹회사코드*/ , RFND_RCP_NO /*환불접수번호*/ , HIST_CH_DTM , RVE_CO_CD /*수납회사코드*/ , CST_NO /*고객번호*/ , CNTR_NO /*계약번호*/ , CNTR_SN /*계약일련번호*/ , CNTR_CH_RCP_ID /*계약변경접수ID*/ , CNTR_CH_SN /*계약변경일련번호*/ , RFND_RCP_OG_TP_CD /*환불접수조직유형코드*/ , RFND_RCP_PRTNR_NO /*환불접수파트너번호*/ , RFND_RCP_PH_CD /*환불접수경로코드*/ , RFND_RQDT /*환불요청일자*/ , RFND_FSH_DT /*환불완료일자*/ , RTNGD_FSH_DT /*반품완료일자*/ , CST_SV_ASN_NO /*고객서비스배정번호*/ , RFND_CSH_AMT /*환불현금금액*/ , RFND_CARD_AMT /*환불카드금액*/ , RFND_BLTF_AMT /*환불전금금액*/ , RFND_DDTN_AMT /*환불공제금액*/ , RFND_FEE_AMT /*환불수수료금액*/ , RFND_PSB_RES_AMT /*환불가능잔여금액*/ , TOT_RFND_ET_AMT /*총환불예상금액*/ , EX_RFND_RSON_CN /*예외환불사유내용*/ , RFND_STAT_CD /*환불상태코드*/ , RFND_RVE_DT /*환불수납일자*/ , RFND_PERF_DT /*환불실적일자*/ , RFND_DSB_DT /*환불지급일자*/ , RFND_PROCS_CN /*환불처리내용*/ , RFND_PSIC_OPN_CN /*환불담당자의견내용*/ , RFND_PSIC_RJ_RSON_CN /*환불담당자반려사유내용*/ , RFND_PROCS_USR_ID /*환불처리사용자ID*/
