mapper.select:
SELECT RRRB.CST_NO /* 고객번호 */ , RRRB.CNTR_NO /* 계약번호 */ , CCB.CST_KNM/* 고객성명 */ , CCB.CRAL_LOCARA_TNO /* 휴대전화번호 앞자리 */ , CCB.MEXNO_ENCR /* 휴대전화번호 중간자리 */ , CCB.CRAL_IDV_TNO /* 휴대전화번호 끝자리 */ , '' AS MPNO /* 휴대전화번호 */ , '' AS TNO /* 전화번호*/ , PPB.PD_NM/* 계약상품 */ , SCD.SELL_TP_CD/* 유형용도 */ , F_CMZ_CD_NM(#{session.tenantId}, 'SELL_TP_CD', SCD.SELL_TP_CD, #{session.langId}) AS SELL_TP_NM , TO_CHAR(TO_DATE(RRRB.RFND_RQDT, 'YYYYMMDD'), 'YYYY.MM.DD') AS RFND_RQDT/* 환불일자 */ , TO_CHAR(TO_DATE(RRRB.RFND_DSB_DT, 'YYYYMMDD'), 'YYYY.MM.DD') AS RFND_DSB_DT/* 지급일 */ , RRD.DP_AMT/* 결제금액 */ , RRD.CARD_DP_AMT , RRD.CSH_DP_AMT , RRRD.RFND_SLPNO/* 전표 */ , RRRD.RFND_DDTN_AMT/* 공제금액 */ , RRRD.RFND_AK_AMT/* 실지급액 */ , RRRD.RFND_DSB_DV_CD/* 지급구분 */ , RRRD.CARD_RFND_FNIT_CD/* 카드구분 */ , (SELECT FNIT_NM FROM TB_RVDW_FNIT_CD WHERE FNIT_DV_CD = '2' AND FNIT_CD = RRRD.CARD_RFND_FNIT_CD) AS CARD_RFND_FNIT_NM , RRRD.CARD_RFND_CRCDONR_NM/* 카드주명 */ , RRRD.CARD_RFND_FEE/* 수수료액 */ , RRRD.CARD_RFND_CRCDNO_ENCR/* 카드번호 */ , RRRD.CARD_RFND_CRDCD_APRNO/* 승인번호 */ , '' AS TMP3/* 유효기간(년월) (RRRD.환불카드유효기간 필드추가필요) */ , RRRD.CARD_RFND_CRDCD_ISTM_MCN/* 할부(개월) */ , RRRD.CSH_RFND_FNIT_CD/* 지급은행 */ , F_CMZ_CD_NM(#{session.tenantId}, 'BNK_CD', RRRD.CSH_RFND_FNIT_CD, #{session.langId}) AS CSH_RFND_FNIT_NM , RRRD.CSH_RFND_ACNO_ENCR/* 계좌번호 */ , RRRD.RFND_RSON_CN/* 환불사유 */ , SSCBD.PART_AMT /*부품비*/ , SSCBD.TCFEE /*기술비*/ , SSCBD.TRCS /*출장비*/ , SSCBD.ETC_CS /*기타*/ , SSCBD.PART_AMT + SSCBD.TCFEE + SSCBD.TRCS + SSCBD.ETC_CS AS RVE_IZ_AMT FROM TB_RVDW_RFND_RCP_BAS RRRB /* 환불접수기본 */ INNER JOIN TB_RVDW_RFND_RCP_DTL RRRD /* 환불접수상세 */ ON RRRB.DTA_DL_YN = 'N' AND RRRD.DTA_DL_YN = 'N' AND RRRB.KW_GRP_CO_CD = RRRD.KW_GRP_CO_CD /* 교원그룹회사코드 = 교원그룹회사코드 */ AND RRRB.RFND_RCP_NO = RRRD.RFND_RCP_NO /* 환불접수번호 = 환불접수번호 */ INNER JOIN TB_SSCT_CNTR_BAS SCB /* 계약기본 */ ON SCB.DTA_DL_YN = 'N' AND RRRB.CNTR_NO = SCB.CNTR_NO /* 계약번호 = 계약번호 */ INNER JOIN TB_SSCT_CNTR_DTL SCD /* 계약상세 */ ON SCD.DTA_DL_YN = 'N' AND RRRB.CNTR_NO = SCD.CNTR_NO /* 계약번호 = 계약번호 */ AND RRRB.CNTR_SN = SCD.CNTR_SN /* 계약상세번호 = 계약상세번호 */ LEFT JOIN TB_PDBS_PD_BAS PPB /* 상품기본 */ ON PPB.DTA_DL_YN = 'N' AND SCD.BASE_PD_CD = PPB.PD_CD LEFT JOIN TB_CUBS_CST_BAS CCB /* 고객기본 */ ON CCB.DTA_DL_YN = 'N' AND SCB.CNTR_CST_NO = CCB.CST_NO LEFT JOIN (SELECT KW_GRP_CO_CD , CNTR_NO , CNTR_SN , SUM(DP_AMT) AS DP_AMT /* 결제금액 */ , SUM(DECODE(RRD.DP_MES_CD, '02', RRD.DP_AMT, 0 )) AS CARD_DP_AMT , SUM(DECODE(RRD.DP_MES_CD, '01', RRD.DP_AMT, 0 )) AS CSH_DP_AMT FROM TB_RVDW_RVE_DTL RRD /* 수납상세 */ WHERE RRD.DTA_DL_YN = 'N' GROUP BY KW_GRP_CO_CD , CNTR_NO , CNTR_SN ) RRD ON RRRB.KW_GRP_CO_CD = RRD.KW_GRP_CO_CD /* 교원그룹회사코드 = 교원그룹회사코드 */ AND RRRB.CNTR_NO = RRD.CNTR_NO /* 계약번호 = 계약번호 */ AND RRRB.CNTR_SN = RRD.CNTR_SN /* 계약일련번호 = 계약일련번호 */ LEFT JOIN (SELECT CNTR_NO , CNTR_SN , CST_SV_ASN_NO , PART_AMT , TCFEE , TRCS , ETC_CS FROM (SELECT CNTR_NO , CNTR_SN , CST_SV_ASN_NO , CS_BIL_ATC_CD , BIL_OJ_AMT FROM WSMDBS.TB_SVPD_SV_CS_BIL_DTL WHERE 1=1 AND DTA_DL_YN = 'N' ) PIVOT(SUM(BIL_OJ_AMT) FOR CS_BIL_ATC_CD IN ('10' AS PART_AMT , '20' AS TCFEE , '30' AS TRCS , '50' AS ETC_CS ))) SSCBD ON RRRB.CNTR_NO = SSCBD.CNTR_NO AND RRRB.CNTR_SN = SSCBD.CNTR_SN WHERE 1 = 1 AND RRRB.RFND_RQDT BETWEEN #{wkStartDtm} AND #{wkEndDtm}
AND SCB.COPN_DV_CD = #{rfndDvCd} /* 환불구분 01.전체, 02.부분 */
AND OPB.OG_ID = #{ogId}
