SELECT MT.FNIT_CD , MT.FNIT_NM FROM TB_RVDW_FNIT_CD MT WHERE 1 = 1 AND MT.FNIT_DG_CD_YN = 'Y' AND MT.FNIT_DV_CD = 1 AND MT.DTA_DL_YN = 'N' AND MT.KFTC_CD_YN = 'Y' AND MT.FNIT_CD IN (SELECT FNIT_CD FROM TB_RVDW_FNIT_FEE_BAS LT WHERE LT.FNIT_CD = MT.FNIT_CD AND LT.DTA_DL_YN = 'N' AND LT.FNIT_FEE_TP_CD = '1' AND LT.VNCO_DV_CD = '002' AND LT.VL_STRTDT <= TO_CHAR(SYSDATE, 'YYYYMMDD') AND LT.VL_ENDDT >= TO_CHAR(SYSDATE, 'YYYYMMDD') )
AND MT.FNIT_CD = #{fnitCd}
ORDER BY MT.FNIT_CD

SELECT TO_CHAR(TO_DATE(A1.CNTR_CH_RCP_DTM, 'YYYYMMDDHH24MISS'), 'YYYYMMDD') RCT_DT /*접수일자*/ , TO_CHAR(TO_DATE(A1.CNTR_CH_RCP_DTM, 'YYYYMMDDHH24MISS'), 'HH24MISS') RCT_TM /*접수시간*/ , A1.CH_RCP_USR_ID /*변경접수사용자ID*/ , ( SELECT MAX(A2.PRTNR_KNM) FROM TB_OGBS_PRTNR_BAS A2 WHERE A2.PRTNR_NO = A1.CH_RCP_USR_ID ) PSIC_NM /*담당자명*/ , B1.DTL_CNTR_NO CNTR_NO /*계약번호*/ , B1.DTL_CNTR_SN CNTR_SN /*계약일련번호*/ , ( SELECT A2.SELL_TP_CD FROM TB_SSCT_CNTR_DTL A2 WHERE A2.DTA_DL_YN = 'N' AND A2.CNTR_NO = B1.DTL_CNTR_NO AND A2.CNTR_SN = B1.DTL_CNTR_SN ) SELL_TP_CD /*판매유형코드*/ , ( SELECT B2.PD_NM FROM TB_SSCT_CNTR_DTL A2 JOIN TB_PDBS_PD_BAS B2 ON B2.PD_CD = A2.BASE_PD_CD WHERE A2.DTA_DL_YN = 'N' AND A2.CNTR_NO = B1.DTL_CNTR_NO AND A2.CNTR_SN = B1.DTL_CNTR_SN ) PD_NM /*상품명*/ , ( SELECT B2.CRAL_LOCARA_TNO FROM TB_SSCT_CNTR_BAS A2 JOIN TB_CUBS_CST_BAS B2 ON B2.CST_NO = A2.CNTR_CST_NO WHERE A2.DTA_DL_YN = 'N' AND A2.CNTR_NO = B1.DTL_CNTR_NO ) CRAL_LOCARA_TNO /*휴대지역전화번호*/ , ( SELECT B2.MEXNO_ENCR FROM TB_SSCT_CNTR_BAS A2 JOIN TB_CUBS_CST_BAS B2 ON B2.CST_NO = A2.CNTR_CST_NO WHERE A2.DTA_DL_YN = 'N' AND A2.CNTR_NO = B1.DTL_CNTR_NO ) MEXNO_ENCR /*휴대전화국번호암호화*/ , ( SELECT B2.CRAL_IDV_TNO FROM TB_SSCT_CNTR_BAS A2 JOIN TB_CUBS_CST_BAS B2 ON B2.CST_NO = A2.CNTR_CST_NO WHERE A2.DTA_DL_YN = 'N' AND A2.CNTR_NO = B1.DTL_CNTR_NO ) CRAL_IDV_TNO /*휴대개별전화번호*/ , '' MPNO /*휴대전화번호*/ , ( SELECT AA1.FNIT_NM FROM ( SELECT C2.FNIT_NM , ROW_NUMBER() OVER(PARTITION BY A2.DTL_CNTR_NO, A2.DTL_CNTR_SN ORDER BY A2.VL_STRT_DTM DESC) AS RN FROM TB_SSCT_CNTR_STLM_REL A2 JOIN TB_SSCT_CNTR_STLM_BAS B2 ON B2.DTA_DL_YN = 'N' AND B2.CNTR_STLM_ID = A2.BF_CNTR_STLM_ID JOIN TB_RVDW_FNIT_CD C2 ON C2.FNIT_CD = B2.BNK_CD WHERE A2.CNTR_STLM_ID = D1.CNTR_STLM_ID AND TO_CHAR(SYSDATE, 'YYYYMMDD') BETWEEN A2.VL_STRT_DTM AND A2.VL_END_DTM AND A2.VL_END_DTM = '99991231235959' AND C2.FNIT_CD = CASE WHEN B2.DP_TP_CD = '0102' THEN LPAD(B2.BNK_CD, 3, 0) WHEN B2.DP_TP_CD = '0203' THEN LPAD(B2.CDCO_CD, 3, 0) END AND C2.FNIT_DV_CD = CASE WHEN B2.DP_TP_CD = '0102' THEN '1' WHEN B2.DP_TP_CD = '0203' THEN '2' END AND TO_CHAR(SYSDATE, 'YYYYMMDD') BETWEEN C2.VL_STRTDT AND C2.VL_ENDDT ) AA1 WHERE AA1.RN = 1 ) BFCH_FNIT_NM /*변경전금융기관명*/ , ( SELECT AA1.BFCH_ACNO_CDNO FROM ( SELECT CASE WHEN A2.DP_TP_CD = '0102' THEN B2.ACNO_ENCR WHEN A2.DP_TP_CD = '0203' THEN B2.CRCDNO_ENCR END BFCH_ACNO_CDNO , ROW_NUMBER() OVER(PARTITION BY A2.DTL_CNTR_NO, A2.DTL_CNTR_SN ORDER BY A2.VL_STRT_DTM DESC) AS RN FROM TB_SSCT_CNTR_STLM_REL A2 JOIN TB_SSCT_CNTR_STLM_BAS B2 ON B2.DTA_DL_YN = 'N' AND B2.CNTR_STLM_ID = A2.BF_CNTR_STLM_ID JOIN TB_RVDW_FNIT_CD C2 ON C2.FNIT_CD = B2.BNK_CD WHERE A2.CNTR_STLM_ID = D1.CNTR_STLM_ID AND TO_CHAR(SYSDATE, 'YYYYMMDD') BETWEEN A2.VL_STRT_DTM AND A2.VL_END_DTM AND A2.VL_END_DTM = '99991231235959' ) AA1 WHERE AA1.RN = 1 ) BFCH_ACNO_CDNO /*변경전계좌카드번호*/ , ( SELECT AA1.OWR_KNM FROM ( SELECT B2.OWR_KNM , ROW_NUMBER() OVER(PARTITION BY A2.DTL_CNTR_NO, A2.DTL_CNTR_SN ORDER BY A2.VL_STRT_DTM DESC) AS RN FROM TB_SSCT_CNTR_STLM_REL A2 JOIN TB_SSCT_CNTR_STLM_BAS B2 ON B2.DTA_DL_YN = 'N' AND B2.CNTR_STLM_ID = A2.BF_CNTR_STLM_ID JOIN TB_RVDW_FNIT_CD C2 ON C2.FNIT_CD = B2.BNK_CD WHERE A2.CNTR_STLM_ID = D1.CNTR_STLM_ID AND TO_CHAR(SYSDATE, 'YYYYMMDD') BETWEEN A2.VL_STRT_DTM AND A2.VL_END_DTM AND A2.VL_END_DTM = '99991231235959' )AA1 WHERE AA1.RN = 1 ) BFCH_OWR_KNM /*변경전소유자한글명*/ , ( SELECT A2.FNIT_NM FROM TB_RVDW_FNIT_CD A2 WHERE A2.FNIT_CD = CASE WHEN D1.DP_TP_CD = '0102' THEN LPAD(D1.BNK_CD, 3, 0) WHEN D1.DP_TP_CD = '0203' THEN LPAD(D1.CDCO_CD, 3, 0) END AND A2.FNIT_DV_CD = CASE WHEN D1.DP_TP_CD = '0102' THEN '1' WHEN D1.DP_TP_CD = '0203' THEN '2' END AND TO_CHAR(SYSDATE, 'YYYYMMDD') BETWEEN A2.VL_STRTDT AND A2.VL_ENDDT ) AFCH_FNIT_NM /*변경후금융기관명*/ , CASE WHEN D1.DP_TP_CD = '0102' THEN D1.ACNO_ENCR WHEN D1.DP_TP_CD = '0203' THEN D1.CRCDNO_ENCR END AFCH_ACNO_CDNO /*변경후계좌카드번호*/ , D1.OWR_KNM AFCH_OWR_KNM /*변경후소유자한글명*/ , D1.MPY_BSDT FNT_STPL_D /*이체약정일*/ , E1.TRS_DTM AFTN_EVID_FSH_DT /*자동이체증빙완료일자*/ , CASE WHEN E1.TRS_DTM IS NOT NULL THEN 'Y' ELSE 'N' END AFTN_EVID_FSH_YN /*자동이체증빙완료여부*/ , D1.FNIT_APR_RS_CD /*금융기관승인결과코드*/ , F_CMZ_CD_NM(#{session.tenantId}, 'SELL_TP_CD', ( SELECT A2.SELL_TP_CD FROM TB_SSCT_CNTR_DTL A2 WHERE A2.DTA_DL_YN = 'N' AND A2.CNTR_NO = B1.DTL_CNTR_NO AND A2.CNTR_SN = B1.DTL_CNTR_SN ) ) SELL_TP_CD_NM /*판매유형코드명*/ FROM TB_SSCT_CNTR_CH_RCP_BAS A1 /*계약변경접수기본*/ JOIN TB_SSCT_CNTR_CH_RCP_DTL B1 /*계약변경접수상세*/ ON B1.DTA_DL_YN = 'N' AND B1.CNTR_CH_RCP_ID = A1.CNTR_CH_RCP_ID JOIN TB_SSCT_CNTR_STLM_BAS D1 /*계약결제기본*/ ON D1.DTA_DL_YN = 'N' AND D1.CNTR_STLM_ID = B1.CNTR_STLM_ID JOIN ( SELECT E1.CNTR_NO , E1.CNTR_SN , E1.APR_DT , E1.BNK_CD , E1.TRS_DTM , ROW_NUMBER() OVER ( PARTITION BY E1.CNTR_NO, E1.CNTR_SN ORDER BY E1.APR_DT DESC ) RN FROM TB_RVCL_AC_FNT_EVID_MTRS_IZ E1 WHERE E1.DTA_DL_YN = 'N' ) E1 ON E1.RN = 1 AND E1.CNTR_NO = B1.DTL_CNTR_NO AND E1.CNTR_SN = B1.DTL_CNTR_SN AND E1.APR_DT = TO_CHAR(TO_DATE(A1.CNTR_CH_RCP_DTM, 'YYYYMMDDHH24MISS'), 'YYYYMMDD') AND E1.BNK_CD = D1.BNK_CD WHERE A1.DTA_DL_YN = 'N' AND A1.CNTR_CH_TP_CD = '301' /*자동이체변경*/
AND A1.CNTR_CH_RCP_DTM BETWEEN #{rctDt}||'000000' AND #{rctDt}||'235959'
AND B1.DTL_CNTR_NO = #{cntrNo}
AND B1.DTL_CNTR_SN = #{cntrSn}
AND A1.CH_RCP_USR_ID = #{chRcpUsrId}
AND E1.TRS_DTM IS NOT NULL
AND E1.TRS_DTM IS NULL
AND D1.OWR_KNM LIKE '%'||#{owrKnm}||'%'
