mapper.select:
SELECT T1.CNTR_NO /* 계약번호 */ , T1.CNTR_SN /* 계약일련번호 */ , T1.CNTR_NO ||'-'|| T1.CNTR_SN AS CNTR_DTL_NO /* 계약상세번호 */ , T5.CST_KNM /* 고객명(한글) */ , T1.SL_CTR_STRT_YM /* 조정시작년월 */ , T1.SL_CTR_END_YM /* 조정종료년월 */ , T4.PD_CD /* 제품코드 */ , T4.PD_NM /* 제품명 */ , T1.SL_CTR_SELL_TP_CD /* 매출조정판매유형*/ , T3.SELL_TP_DTL_CD /* 판매유형상세코드 - 20231006 조회 불가로 컬럼 추가 */ , T1.SL_CTR_MTR_DV_CD /* 매출조정자료구분코드 */ , T1.SL_CTR_MTR_TP_CD /* 매출조정자료유형코드 */ , T1.SL_CTR_DV_CD /* 매출조정구분코드 */ , T1.SL_CTR_TP_CD /* 매출조정유형코드 */ , T1.SL_CTR_DSC_TP_CD /* 매출조정할인유형코드 */ , T1.CAN_AF_OJ_YN /* 취소후적용 - default N */ , T1.SL_CTR_AMT /* 매출조정금액 */ , NVL(T1.SL_CTR_WO_EXMP_AMT, 0) AS SL_CTR_WO_EXMP_AMT /* 매출전액면제금액 */ , NVL(T1.SL_CTR_PTRM_EXMP_AMT , 0 ) AS SL_CTR_PTRM_EXMP_AMT /* 매출조정기간면제금액 */ , T1.SL_CTR_RMK_CN /* 조정비고내용 - 사유 */ , T1.FST_RGST_DTM AS SL_CTR_PRCSDT /* 등록일자 */ , NVL( (SELECT B1.USR_NM FROM T_CMY_USR_M A1 JOIN T_CMP_USR_ACO_M B1 ON B1.USR_ID = A1.USR_ID WHERE A1.USR_ID = T1.FNL_MDFC_USR_ID AND A1.TENANT_ID = #{session.tenantId} ), (SELECT A1.USR_NM FROM T_CMP_USR_ACO_M A1 WHERE A1.EPNO = T1.FNL_MDFC_USR_ID ) ) USR_NM , NVL( (SELECT B1.EPNO FROM T_CMY_USR_M A1 JOIN T_CMP_USR_ACO_M B1 ON B1.USR_ID = A1.USR_ID WHERE A1.USR_ID = T1.FNL_MDFC_USR_ID AND A1.TENANT_ID = #{session.tenantId} ), (SELECT A1.EPNO FROM T_CMP_USR_ACO_M A1 WHERE A1.EPNO = T1.FNL_MDFC_USR_ID ) ) FNL_MDFC_USR_ID FROM TB_RVDW_SL_CTR_BAS T1 /* 매출조정기본 */ JOIN TB_SSCT_CNTR_DTL T3 /* 계약상세 */ ON T3.CNTR_NO = T1.CNTR_NO AND T3.CNTR_SN = T1.CNTR_SN AND T3.DTA_DL_YN = 'N' JOIN TB_SSCT_CNTR_BAS T2 /* 계약기본 */ ON T1.CNTR_NO = T2.CNTR_NO AND T2.DTA_DL_YN = 'N' JOIN TB_PDBS_PD_BAS T4 /* 상품기본 */ ON T3.BASE_PD_CD = T4.PD_CD AND T4.DTA_DL_YN = 'N' JOIN TB_CUBS_CST_BAS T5 /* 고객기본 */ ON T2.CNTR_CST_NO = T5.CST_NO AND T5.DTA_DL_YN = 'N' WHERE 1=1
AND T1.SL_CTR_SELL_TP_CD = #{sellTp}
AND T3.SELL_TP_DTL_CD = #{sellTpDtlCd}
AND T1.SL_CTR_STRT_YM <= #{perfFshDtm} AND T1.SL_CTR_END_YM >= #{perfStrtDtm}
AND T1.CNTR_NO = #{cntrNo}
AND T1.CNTR_SN = #{cntrSn}
AND T1.SL_CTR_DSC_TP_CD = #{ctrTp}
AND T1.SL_CTR_MTR_DV_CD = #{mtrDv}
AND T1.SL_CTR_DV_CD = #{ctrDv}
AND T1.SL_CTR_TP_CD = #{dsc}
AND T1.FST_RGST_USR_ID = #{prtnrNo}
AND T1.FST_RGST_DTM >= #{slCtrPrcsStrtDt}||'000000'
AND T1.FST_RGST_DTM <= #{slCtrPrcsFshDt}||'235959'

SELECT COUNT(1)
  FROM TB_RVDW_SL_CTR_BAS T1 /* 매출조정기본 */
 WHERE 1=1
   AND CNTR_NO = #{cntrNo}
   AND CNTR_SN = #{cntrSn}
   AND SL_CTR_SELL_TP_CD = #{slCtrSellTpCd}
   AND SL_CTR_MTR_DV_CD = #{slCtrMtrDvCd}
   AND SL_CTR_MTR_TP_CD = #{slCtrMtrTpCd}
   AND SL_CTR_STRT_YM = #{slCtrStrtYm}

mapper.insert:
MERGE INTO TB_RVDW_SL_CTR_BAS /* 매출조정기본 */ USING DUAL ON ( CNTR_NO = #{cntrNo} AND CNTR_SN = #{cntrSn} AND SL_CTR_STRT_YM = #{slCtrStrtYm} AND SL_CTR_SELL_TP_CD = #{slCtrSellTpCd} /* 매출조정판매유형코드 */ AND SL_CTR_MTR_DV_CD = #{slCtrMtrDvCd} /* 매출조정자료구분코드 */ AND SL_CTR_MTR_TP_CD = #{slCtrMtrTpCd} /* 매출조정자료유형코드 */ ) WHEN MATCHED THEN UPDATE SET SL_CTR_END_YM = #{slCtrEndYm}/* 매출조정종료년월 */ , SL_CTR_DV_CD = #{slCtrDvCd}/* 매출조정구분코드 */ , SL_CTR_TP_CD = #{slCtrTpCd}/* 매출조정유형코드 */ , SL_CTR_DSC_TP_CD = #{slCtrDscTpCd}/* 매출조정할인유형코드 */ , CAN_AF_OJ_YN = #{canAfOjYn}/* 취소이후대상여부 */ , SL_CTR_AMT = #{slCtrAmt}/* 매출조정금액 */ , SL_CTR_WO_EXMP_AMT = #{slCtrWoExmpAmt} /* 매출조정전체면제금액 */ , SL_CTR_PTRM_EXMP_AMT = #{slCtrPtrmExmpAmt} /* 매출조정기간면제금액 */ , SL_CTR_RMK_CN = #{slCtrRmkCn}/* 매출조정비고내용 */ , SL_CTR_PROCS_YN = #{slCtrProcsYn}/* 매출조정처리여부 */ , SL_CTR_PRCSDT = #{slCtrPrcsdt} /* 매출조정처리일자 */
WHEN NOT MATCHED THEN INSERT ( CNTR_NO /* 계약번호 */ , CNTR_SN /* 계약일련번호 */ , SL_CTR_SELL_TP_CD /* 매출조정판매유형코드 */ , SL_CTR_MTR_DV_CD /* 매출조정자료구분코드 */ , SL_CTR_MTR_TP_CD /* 매출조정자료유형코드 */ , SL_CTR_STRT_YM /* 매출조정시작년월 */ , SL_CTR_END_YM /* 매출조정종료년월 */ , SL_CTR_DV_CD /* 매출조정구분코드 */ , SL_CTR_TP_CD /* 매출조정유형코드 */ , SL_CTR_DSC_TP_CD /* 매출조정할인유형코드 */ , CAN_AF_OJ_YN /* 취소이후대상여부 */ , SL_CTR_AMT /* 매출조정금액 */ , SL_CTR_WO_EXMP_AMT /* 매출조정전체면제금액 */ , SL_CTR_PTRM_EXMP_AMT /* 매출조정기간면제금액 */ , SL_CTR_RMK_CN /* 매출조정비고내용 */ , SL_CTR_PROCS_YN /* 매출조정처리여부 */ , SL_CTR_PRCSDT /* 매출조정처리일자 */ , DTA_DL_YN /* 데이터삭제여부*/
) VALUES ( #{cntrNo} /* 계약번호 */ , #{cntrSn} /* 계약일련번호 */ , #{slCtrSellTpCd} /* 매출조정판매유형코드 */ , #{slCtrMtrDvCd} /* 매출조정자료구분코드 */ , #{slCtrMtrTpCd} /* 매출조정자료유형코드 */ , #{slCtrStrtYm} /* 매출조정시작년월 */ , #{slCtrEndYm} /* 매출조정종료년월 */ , #{slCtrDvCd} /* 매출조정구분코드 */ , #{slCtrTpCd} /* 매출조정유형코드 */ , #{slCtrDscTpCd} /* 매출조정할인유형코드 */ , #{canAfOjYn} /* 취소이후대상여부 */ , #{slCtrAmt} /* 매출조정금액 */ , #{slCtrWoExmpAmt} /* 매출조정전체면제금액 */ , #{slCtrPtrmExmpAmt} /* 매출조정기간면제금액 */ , #{slCtrRmkCn} /* 매출조정비고내용 */ , #{slCtrProcsYn} /* 매출조정처리여부 */ , #{slCtrPrcsdt} /* 매출조정처리일자 */ , 'N' /* 데이터삭제여부*/
)

INSERT INTO TB_RVDW_SL_CTR_BAS_HIST ( CNTR_NO /* 계약번호 */ , CNTR_SN /* 계약일련번호 */ , SL_CTR_SELL_TP_CD /* 매출조정판매유형코드 */ , SL_CTR_MTR_DV_CD /* 매출조정자료구분코드 */ , SL_CTR_MTR_TP_CD /* 매출조정자료유형코드 */ , SL_CTR_STRT_YM /* 매출조정시작년월 */ , SL_CTR_CH_SN /* 매출조정변경일련번호 : default 1 */ , SL_CTR_END_YM /* 매출조정종료년월 */ , SL_CTR_DV_CD /* 매출조정구분코드 */ , SL_CTR_TP_CD /* 매출조정유형코드 */ , SL_CTR_DSC_TP_CD /* 매출조정할인유형코드 */ , CAN_AF_OJ_YN /* 취소이후대상여부 */ , SL_CTR_AMT /* 매출조정금액 */ , SL_CTR_WO_EXMP_AMT /* 매출조정전체면제금액 */ , SL_CTR_PTRM_EXMP_AMT /* 매출조정기간면제금액 */ , SL_CTR_RMK_CN /* 매출조정비고내용 */ , SL_CTR_PROCS_YN /* 매출조정처리여부 */ , SL_CTR_PRCSDT /* 매출조정처리일자 */ , DTA_DL_YN /* 데이터삭제여부*/
) VALUES ( #{cntrNo} /* 계약번호 */ , #{cntrSn} /* 계약일련번호 */ , #{slCtrSellTpCd} /* 매출조정판매유형코드 */ , #{slCtrMtrDvCd} /* 매출조정자료구분코드 */ , #{slCtrMtrTpCd} /* 매출조정자료유형코드 */ , #{slCtrStrtYm} /* 매출조정시작년월 */ , (SELECT NVL(MAX(SL_CTR_CH_SN),0) + 1 FROM TB_RVDW_SL_CTR_BAS_HIST WHERE 1 = 1 AND CNTR_NO = #{cntrNo} AND CNTR_SN = #{cntrSn} AND SL_CTR_SELL_TP_CD = #{slCtrSellTpCd} AND SL_CTR_MTR_DV_CD = #{slCtrMtrDvCd} AND SL_CTR_MTR_TP_CD = #{slCtrMtrTpCd} AND SL_CTR_STRT_YM = #{slCtrStrtYm}) , #{slCtrEndYm} /* 매출조정종료년월 */ , #{slCtrDvCd} /* 매출조정구분코드 */ , #{slCtrTpCd} /* 매출조정유형코드 */ , #{slCtrDscTpCd} /* 매출조정할인유형코드 */ , #{canAfOjYn} /* 취소이후대상여부 */ , #{slCtrAmt} /* 매출조정금액 */ , #{slCtrWoExmpAmt} /* 매출조정전체면제금액 */ , #{slCtrPtrmExmpAmt} /* 매출조정기간면제금액 */ , #{slCtrRmkCn} /* 매출조정비고내용 */ , #{slCtrProcsYn} /* 매출조정처리여부 */ , #{slCtrPrcsdt} /* 매출조정처리일자 */ , 'N' /* 데이터삭제여부*/
)

mapper.update:
UPDATE TB_CBCL_WELLS_SL_CNFM_BAS /* 매출확정T */
   SET SL_CTR_AMT = #{slCtrAmt} /* 매출조정금액 */

 WHERE 1=1
   AND CNTR_NO = #{cntrNo}
   AND CNTR_SN = #{cntrSn}
   AND DTA_DL_YN = 'N'

