mapper.insert:
INSERT INTO TB_RVDW_SL_CTR_BAS (
	  CNTR_NO
	, CNTR_SN
	, SL_CTR_SELL_TP_CD
	, SL_CTR_MTR_DV_CD
	, SL_CTR_MTR_TP_CD
	, SL_CTR_STRT_YM
	, SL_CTR_END_YM
	, SL_CTR_DV_CD
	, SL_CTR_DSC_TP_CD
	, SL_CTR_AMT
	, SL_CTR_RMK_CN

) VALUES (
	  #{cntrNo}
	, #{cntrSn}
	, #{orderType}
	, '1'
	, '1'
	, #{controlYear}||#{controlMonth}
	, #{controlYear}||#{controlMonth}
	, #{controlDivide}
	, #{controlType}
	, #{controlAmount}
	, #{controlReason}

)

INSERT INTO TB_RVDW_SL_CTR_BAS_HIST ( CNTR_NO , CNTR_SN , SL_CTR_SELL_TP_CD , SL_CTR_MTR_DV_CD , SL_CTR_MTR_TP_CD , SL_CTR_STRT_YM , SL_CTR_CH_SN , SL_CTR_END_YM , SL_CTR_DV_CD , SL_CTR_DSC_TP_CD , SL_CTR_AMT , SL_CTR_RMK_CN
) SELECT CNTR_NO , CNTR_SN , SL_CTR_SELL_TP_CD , SL_CTR_MTR_DV_CD , SL_CTR_MTR_TP_CD , SL_CTR_STRT_YM , (SELECT NVL(MAX(SL_CTR_CH_SN),0) + 1 FROM TB_RVDW_SL_CTR_BAS_HIST WHERE 1 = 1 AND CNTR_NO = #{cntrNo} AND CNTR_SN = #{cntrSn} AND SL_CTR_SELL_TP_CD = #{orderType} AND SL_CTR_MTR_DV_CD = '1' AND SL_CTR_MTR_TP_CD = '1' AND SL_CTR_STRT_YM = #{controlYear}||#{controlMonth} ) , SL_CTR_END_YM , SL_CTR_DV_CD , SL_CTR_DSC_TP_CD , SL_CTR_AMT , SL_CTR_RMK_CN
FROM TB_RVDW_SL_CTR_BAS WHERE 1 = 1 AND CNTR_NO = #{cntrNo} AND CNTR_SN = #{cntrSn} AND SL_CTR_SELL_TP_CD = #{orderType} AND SL_CTR_MTR_DV_CD = '1' AND SL_CTR_MTR_TP_CD = '1' AND SL_CTR_STRT_YM = #{controlYear}||#{controlMonth}
